INSERT INTO `ger_impostos` VALUES (30, 'Isento nos Termos da alínea e) do nº1 do artigo 12º do CIVA', 'M14', '1', 0.00, 'P', 'AO', 'IVA', 'ISE', '12', 'A locação de bens imóveis destinados a fins habitacionais, designadamente\r\nprédios urbanos, fracções autónomas destes ou terrenos para construção,\r\ncom excepção das prestações de serviços de alojamento efectuadas no\r\nâmbito da actividade hoteleira ou de outras com funções análogas', NULL, NULL);
INSERT INTO `ger_impostos` VALUES (31, 'Isento nos Termos da alínea f) do nº1 do artigo 12º do CIVA', 'M15', '1', 0.00, 'P', 'AO', 'IVA', 'ISE', '12', 'As operações sujeitas ao imposto de SISA, ainda que dele isentas', NULL, NULL);
INSERT INTO `ger_impostos` VALUES (32, 'Isento nos Termos da alínea g) do nº1 do artigo 12º do CIVA', 'M16', '1', 0.00, 'P', 'AO', 'IVA', 'ISE', '12', 'A exploração e a prática de jogos de fortuna ou azar e de diversão social,\r\nbem como as respectivas comissões e todas as operações relacionadas,\r\nquando as mesmas estejam sujeitas a Imposto Especial sobre o Jogos, nos\r\ntermos da legislação aplicável', NULL, NULL);
INSERT INTO `ger_impostos` VALUES (33, 'Isento nos Termos da alínea h) do nº1 do artigo 12º do CIVA', 'M17', '1', 0.00, 'P', 'AO', 'IVA', 'ISE', '12', 'O transporte colectivo de passageiros', NULL, NULL);
INSERT INTO `ger_impostos` VALUES (34, 'Isento nos Termos da alínea i) do nº1 do artigo 12º do CIVA', 'M18', '1', 0.00, 'P', 'AO', 'IVA', 'ISE', '12', 'As operações de intermediação financeira, incluindo a locação financeira,\r\nexceptuando-se aquelas em que uma taxa, ou contraprestação, especifica e\r\npredeterminada é cobrada pelo serviço.', NULL, NULL);
INSERT INTO `ger_impostos` VALUES (35, 'Isento nos Termos da alínea a) do nº2 do artigo 14º do CIVA', 'M85', '1', 0.00, 'P', 'AO', 'IVA', 'ISE', '14', 'A importação de mercadorias ou equipamentos destinadas exclusiva e\r\ndirectamente à execução das operações petrolíferas e mineiras nos termos da', NULL, NULL);
INSERT INTO `ger_impostos` VALUES (36, 'Isento nos Termos da alínea b) do nº2 do artigo 14º do CIVA', 'M86', '1', 0.00, 'P', 'AO', 'IVA', 'ISE', '14', 'No âmbito de relações diplomáticas e consulares, quando a isenção resulte\r\nde tratados e acordos internacionais celebrados pela República de Angola', NULL, NULL);
INSERT INTO `ger_impostos` VALUES (37, 'Isento nos Termos da alínea a) do artigo 15º do CIVA', 'M30', '1', 0.00, 'P', 'AO', 'IVA', 'ISE', '15', 'As transmissões de bens expedidos ou transportados com destino ao\r\nestrangeiro pelo vendedor ou por um terceiro por conta deste', NULL, NULL);
INSERT INTO `ger_impostos` VALUES (38, 'Isento nos Termos da alínea b) do artigo 15º do CIVA', 'M31', '1', 0.00, 'P', 'AO', 'IVA', 'ISE', '15', 'As transmissões de bens de abastecimento postos a bordo das embarcações\r\nque efectuem navegação marítima em alto mar e que assegurem o transporte\r\nremunerado de passageiros ou o exercício de uma actividade comercial,\r\nindustrial ou de pesca;', NULL, NULL);
INSERT INTO `ger_impostos` VALUES (39, 'Isento nos Termos da alínea c) do artigo 15º do CIVA', 'M32', '1', 0.00, 'P', 'AO', 'IVA', 'ISE', '15', 'As transmissões de bens de abastecimento postos a bordo das aeronaves\r\nutilizadas por companhias de navegação aérea que se dediquem\r\nprincipalmente ao tráfego internacional e que assegurem o transporte\r\nremunerado de passageiros, ou o exercício de uma actividade comercial ou\r\nindustrial', NULL, NULL);
INSERT INTO `ger_impostos` VALUES (40, 'Isento nos Termos da alínea d) do artigo 15º do CIVA', 'M33', '1', 0.00, 'P', 'AO', 'IVA', 'ISE', '15', 'As transmissões de bens de abastecimento postos a bordo das embarcações\r\nde salvamento, assistência marítima, pesca costeira e embarcações de\r\nguerra, quando deixem o país com destino a um porto ou ancoradouro situado\r\nno estrangeiro', NULL, NULL);
INSERT INTO `ger_impostos` VALUES (41, 'Isento nos Termos da alínea e) do artigo 15º do CIVA', 'M34', '1', 0.00, 'P', 'AO', 'IVA', 'ISE', '15', 'As transmissões, transformações, reparações, manutenção, frete e aluguer,\r\nincluindo a locação financeira, de embarcações e aeronaves afectas às\r\ncompanhias de navegação aérea e marítima que se dediquem principalmente\r\nao tráfego internacional, assim como as transmissões de bens de\r\nabastecimento postos a bordo das mesmas e as prestações de serviços\r\nefectuadas com vista à satisfação das suas necessidades directas e da\r\nrespectiva carga;', NULL, NULL);
INSERT INTO `ger_impostos` VALUES (42, 'Isento nos Termos da alínea f) do artigo 15º do CIVA', 'M35', '1', 0.00, 'P', 'AO', 'IVA', 'ISE', '15', 'As transmissões de bens efectuadas no âmbito de relações diplomáticas e\r\nconsulares cuja isenção resulte de acordos e convénios internacionais\r\ncelebrados por Angola;', NULL, NULL);
INSERT INTO `ger_impostos` VALUES (43, 'Isento nos Termos da alínea g) do artigo 15º do CIVA', 'M36', '1', 0.00, 'P', 'AO', 'IVA', 'ISE', '15', 'As transmissões de bens destinados a organismos internacionais\r\nreconhecidos por Angola ou a membros dos mesmos organismos, nos limites\r\ne com as condições fixadas em acordos e convénios internacionais\r\ncelebrados por Angola', NULL, NULL);
INSERT INTO `ger_impostos` VALUES (44, 'Regime transitório', 'M00', '1', 0.00, 'P', 'AO', 'IVA', 'ISE', NULL, NULL, 'T', NULL);
INSERT INTO `ger_impostos` VALUES (45, 'Isento nos termos da alinea a) do nº1 do artigo 16.º do CIVA', 'M90', '1', 0.00, 'P', 'AO', 'IVA', 'ISE', '16', 'As importações de bens que, sob controlo aduaneiro e de acordo com as\r\ndisposições aduaneiras especificamente aplicáveis, sejam postas nos regimes\r\nde zona franca, que sejam introduzidos em armazéns de regimes aduaneiros\r\nou lojas francas, enquanto permanecerem sob tais regimes;', NULL, NULL);
INSERT INTO `ger_impostos` VALUES (46, 'Isento nos termos da alinea b) do nº1 do artigo 16.º do CIVA', 'M91', '1', 0.00, 'P', 'AO', 'IVA', 'ISE', '16', 'As transmissões de bens que sejam expedidos ou transportados para as zonas\r\nou depósitos mencionados na alínea anterior, bem como as prestações de\r\nserviços directamente conexas com tais transmissões;', NULL, NULL);
INSERT INTO `ger_impostos` VALUES (47, 'Isento nos termos da alinea c) do nº1 do artigo 16.º do CIVA', 'M92', '1', 0.00, 'P', 'AO', 'IVA', 'ISE', '16', 'As transmissões de bens que se efectuem nos regimes a que se refere a\r\nalínea a), assim como as prestações de serviços directamente conexas com\r\ntais transmissões, enquanto os bens permanecerem naquelas situações', NULL, NULL);
INSERT INTO `ger_impostos` VALUES (48, 'Isento nos termos da alinea d) do nº1 do artigo 16.º do CIVA', 'M93', '1', 0.00, 'P', 'AO', 'IVA', 'ISE', '16', 'As transmissões de bens que se encontrem nos regimes de trânsito,\r\ndraubaque ou importação temporária e as prestações de serviços\r\ndirectamente conexas com tais operações, enquanto os mesmos forem\r\nconsiderados abrangidos por aqueles regimes', NULL, NULL);
INSERT INTO `ger_impostos` VALUES (49, 'Isento nos termos da alinea e) do nº1 do artigo 16.º do CIVA', 'M94', '1', 0.00, 'P', 'AO', 'IVA', 'ISE', '16', 'A reimportação de bens por quem os exportou, no mesmo estado em que\r\nforam exportados, quando beneficiem de isenção de direitos aduaneiros', NULL, NULL);
INSERT INTO `ger_impostos` VALUES (50, 'Isento nos termos da alínea b) do nº1 do artigo 12.º\r\ndo CIVA', 'M11', '1', 0.00, 'P', 'AO', 'IVA', 'ISE', '12', 'As transmissões de medicamentos destinados exclusivamente a fins\r\nterapêuticos e profilácticos.', NULL, NULL);
