DROP FUNCTION IF EXISTS `fn_Saldo_Caixa`;
delimiter ;;
CREATE FUNCTION `fn_Saldo_Caixa`(`START_DATE` date,`END_DATE` date,`USERNAME` varchar(20),`COMPANY` int)
 RETURNS decimal(18,5)
  DETERMINISTIC
BEGIN
 
 SET @RECEBIDO:=(SELECT IFNULL(SUM(C.MOV_VALOR),0) FROM fin_movimento_caixa C WHERE C.MOV_TRANSACAO='E' 
AND C.MOV_UTILIZADOR=USERNAME AND C.MOV_DATA BETWEEN START_DATE AND END_DATE);
SET @TROCO:=(SELECT IFNULL(SUM(C.MOV_VALOR),0) FROM fin_movimento_caixa C WHERE C.MOV_TRANSACAO='S' 
AND C.MOV_UTILIZADOR=USERNAME AND C.MOV_DATA BETWEEN START_DATE AND END_DATE);
SET @TOTAL:=(@RECEBIDO-@TROCO); 
 RETURN @TOTAL;
END
;;
delimiter ;

