-- ----------------------------
-- Procedure structure for stp_ANTENDIMENTO_TROCA_MESA
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_ANTENDIMENTO_TROCA_MESA`;
delimiter ;;
CREATE PROCEDURE `stp_ANTENDIMENTO_TROCA_MESA`(IN `ATENDIMENTO_ORIGEM_ID` int,IN `MESA_ORIGEM_ID` int, IN FUNCIONARIO_ID INT, ATENDIMENTO_DESTINO_ID INT, MESA_DESTINO_ID INT)
BEGIN
	#Routine body goes here...
   DECLARE CONTA DECIMAL(18,5) DEFAULT 0;
	 
   IF(ATENDIMENTO_DESTINO_ID <= 0) THEN
	   
	   SELECT IFNULL(ATEND_CODIGO, -1) INTO ATENDIMENTO_DESTINO_ID FROM REST_ATENDIMENTO WHERE ATEND_MESA_ID =    MESA_DESTINO_ID AND ATEND_STATUS ="0";
	 END IF;
 
  IF(ATENDIMENTO_DESTINO_ID > 0) THEN # CASO A MESA DE DESTINO JA TENHA CONSUMO 
	
	  # 1. TRANSFERE TODO CONSUMO DO ATENDIMENTO DE ORIGEM PARA O DE DESTINO
	  UPDATE REST_ATENDIMENTO_ARTIGOS SET 
		ATEND_ITEM_CODIGO_ATENDIMENTO = ATENDIMENTO_DESTINO_ID
		WHERE ATEND_ITEM_CODIGO_ATENDIMENTO = ATENDIMENTO_ORIGEM_ID;
		
		# 2. TRANSFERE TODOS OS PEDIDOS DO ATENDIMENTO DE ORIGEM PARA O DE DESTINO
		UPDATE REST_ATENDIMENTO_PEDIDO SET 
		REST_PEDIDO_ATENDIMENTO_ID = ATENDIMENTO_DESTINO_ID, 
    REST_MESA_ID = MESA_DESTINO_ID
		WHERE REST_PEDIDO_ATENDIMENTO_ID = ATENDIMENTO_ORIGEM_ID;
	  
		# 3. ACTUALIZAR O VALOR DO CONSUMO 
		SELECT SUM(ATEND_ITEM_SUBTOTAL) INTO CONTA FROM REST_ATENDIMENTO_ARTIGOS 
		WHERE ATEND_ITEM_CODIGO_ATENDIMENTO =   ATENDIMENTO_DESTINO_ID;
		
		UPDATE REST_ATENDIMENTO SET 
		ATEND_CONTA =  CONTA
		WHERE ATEND_CODIGO = ATENDIMENTO_DESTINO_ID;  
		
		# 4. APAGA O ATENDIMENTO DE ORIGEM		
		DELETE FROM REST_ATENDIMENTO WHERE ATEND_CODIGO = ATENDIMENTO_ORIGEM_ID;
		
		
		CALL stp_REST_MESA_DESOCUPAR(MESA_ORIGEM_ID);
		CALL stp_REST_MESA_OCUPAR(MESA_DESTINO_ID);
	
	ELSE IF(NOT EXISTS(SELECT * FROM REST_ATENDIMENTO WHERE ATEND_MESA_ID = MESA_DESTINO_ID AND ATEND_STATUS ="0")) THEN
	  # 1. ACTUALIZAR A NOVA MESA PARA O ATENDIMENTO
		UPDATE REST_ATENDIMENTO SET 
		ATEND_ATENDENTE = FUNCIONARIO_ID,
		ATEND_MESA_ID = MESA_DESTINO_ID
		WHERE ATEND_CODIGO = ATENDIMENTO_ORIGEM_ID;
		
		# 2. ACTUALIZA A NOVA MESA PARA OS PEDIDO DO ATENDIMENTO
		UPDATE REST_ATENDIMENTO_PEDIDO SET 
    REST_MESA_ID = MESA_DESTINO_ID
		WHERE REST_PEDIDO_ATENDIMENTO_ID = ATENDIMENTO_ORIGEM_ID; 
	  
		 
	CALL stp_REST_MESA_DESOCUPAR(MESA_ORIGEM_ID);
	CALL stp_REST_MESA_OCUPAR(MESA_DESTINO_ID);
	
	END IF;
  END IF;	
  

END
;;
delimiter ;

