-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_AUTO_ORDEM_SERVICO_ADICIONAR`;
delimiter ;;
CREATE PROCEDURE `stp_AUTO_ORDEM_SERVICO_ADICIONAR`(IN `OS_ID` int,IN `DOCUMENT` int,IN `SERIE` int,IN `OS_TYPE` bit,IN `REFERENCE` varchar(20),IN `NUMBER` int,IN `DOC_STATE` bit,IN `TECHNICIAN_ID` int,IN `CAR` int,IN `KM` decimal(18,2),IN `OS_STATUS` int,IN `BOOKER_USER` varchar(20),IN `BOOKING_DATE` datetime,IN `CHECKIN_TECHNICIAN` int, IN `CHECKIN` datetime,IN `DRIVER` varchar(20),IN `CONTACT` varchar(20),IN `STARTED` datetime,IN `FINISHED` datetime,IN `CHECKOUT_TECHNICIAN` int,IN `CHECKOUT` datetime,IN `FLAG` bit,IN `UTILIZADOR` varchar(20), COMPANY_ID INT, PREDICTION_DATE DATE,  FREE_NOTES longtext, ENTITY_ID INT, OUT LSTID INT)
BEGIN
	#Routine body goes here...

DECLARE DOCUMENT_SIMBOL VARCHAR(3) DEFAULT "";
DECLARE DOCUMENT_NUMBER INT DEFAULT 0;
DECLARE ANO_FATURACAO INT DEFAULT 0;
DECLARE ACTIVO INT DEFAULT 1;
DECLARE PREFIXO VARCHAR(12);
DECLARE DOC_TYPE VARCHAR(10) DEFAULT "";

IF(TECHNICIAN_ID <=0) THEN

SET TECHNICIAN_ID = NULL;
ELSE
SELECT GAR_CODIGO INTO TECHNICIAN_ID FROM REST_FUNCIONARIOS WHERE GAR_CODIGO_ENTIDADE = TECHNICIAN_ID;
END IF;

IF(CHECKIN_TECHNICIAN <=0) THEN

SET CHECKIN_TECHNICIAN = NULL;
ELSE
SELECT GAR_CODIGO INTO CHECKIN_TECHNICIAN FROM REST_FUNCIONARIOS WHERE GAR_CODIGO_ENTIDADE = CHECKIN_TECHNICIAN;
END IF;

IF(CHECKOUT_TECHNICIAN <=0) THEN

SET CHECKOUT_TECHNICIAN = NULL;
ELSE
SELECT GAR_CODIGO INTO CHECKOUT_TECHNICIAN FROM REST_FUNCIONARIOS WHERE GAR_CODIGO_ENTIDADE = CHECKOUT_TECHNICIAN;
END IF;
 

IF(OS_ID <=0) THEN

SELECT ORDER_ID INTO OS_ID FROM AUTO_ORDEM_SERVICO WHERE CAR_ID = CAR AND DATE_FORMAT(CHECKIN_DATE, '%Y-%m-%d') = CHECKIN;

END IF;
 
  


IF(OS_ID IS NULL OR OS_ID <=0) THEN


INSERT INTO AUTO_ORDEM_SERVICO(
ORDER_ID,
DOCUMENT_ID,
SERIE_ID,
OS_TYPE,
DOCUMENT_REFERENCE,
DOCUMENT_NUMBER,
DOCUMENT_STATUS,
TECHNICIAN_ID,
CAR_ID,
CAR_KM,
OS_STATUS_ID,
BOOK_BY,
BOOK_DATE,
CHECKIN_TECHNICIAN_ID,
CHECKIN_DATE,
DRIVER_PEOPLE,
DRIVER_CONTACT,
WORK_START_DATE,
WORK_FINISH_DATE,
CHECKOUT_TECHNICIAN_ID,
CHECKOUT_DATE,
NOTES,
ACTIVED,
CREATED_BY,
CREATED_DATE,
UPDATED_BY,
UPDATED_DATE,
BRANCH_ID,
PREDICTION_CLOSING,
BILLING_ENTITY_ID

)VALUES
(
 DEFAULT,
DOCUMENT,
SERIE,
OS_TYPE,
REFERENCE,
NUMBER,
DOC_STATE,
TECHNICIAN_ID,
CAR,
KM,
OS_STATUS,
BOOKER_USER,
BOOKING_DATE,
CHECKIN_TECHNICIAN,
CHECKIN,
DRIVER,
CONTACT,
STARTED,
FINISHED, 
CHECKOUT_TECHNICIAN,
CHECKOUT,
FREE_NOTES,
FLAG,
UTILIZADOR,
NOW(),
UTILIZADOR,
NOW(),
COMPANY_ID,
PREDICTION_DATE,
ENTITY_ID 
);
SELECT LAST_INSERT_ID() INTO OS_ID;  
ELSE
	UPDATE AUTO_ORDEM_SERVICO SET 
	DOCUMENT_ID = DOCUMENT,
	SERIE_ID = SERIE,
	OS_TYPE = OS_TYPE, 
	DOCUMENT_STATUS = DOC_STATE,
	TECHNICIAN_ID = TECHNICIAN_ID,
	CAR_ID = CAR,
	CAR_KM = KM,
	OS_STATUS_ID = OS_STATUS,
	BOOK_BY = BOOKER_USER,
	BOOK_DATE = BOOKING_DATE,
	CHECKIN_TECHNICIAN_ID = CHECKIN_TECHNICIAN,
	CHECKIN_DATE = CHECKIN,
	DRIVER_PEOPLE = DRIVER,
	DRIVER_CONTACT = CONTACT,
	WORK_START_DATE = STARTED,
	WORK_FINISH_DATE = FINISHED, 
	CHECKOUT_DATE = CHECKOUT,
	UPDATED_BY = UTILIZADOR,
	UPDATED_DATE = NOW(),
	ACTIVED = FLAG,
  PREDICTION_CLOSING = PREDICTION_DATE,
  NOTES = FREE_NOTES,
  BILLING_ENTITY_ID = ENTITY_ID,
  CHECKOUT_TECHNICIAN_ID = CHECKOUT_TECHNICIAN
	WHERE ORDER_ID = OS_ID;

END IF;


IF(NUMBER IS NULL OR NUMBER =0) THEN

     SELECT DOCUMENT_NUMBER INTO NUMBER FROM AUTO_ORDEM_SERVICO WHERE ORDER_ID = OS_ID;           
     END IF;

     IF(NUMBER IS NULL OR NUMBER < 1) THEN
			 SELECT DOCUMENT_REFERENCE INTO REFERENCE FROM AUTO_ORDEM_SERVICO WHERE ORDER_ID = OS_ID; 
			 SELECT DOC_SIGLA INTO DOCUMENT_SIMBOL FROM COM_DOCUMENTO WHERE DOC_CODIGO = DOCUMENT;
			 SELECT SER_NUMERACAO, SER_ANO INTO NUMBER, ANO_FATURACAO FROM COM_SERIE_DOCUMENTACAO WHERE SER_CODIGO = SERIE;
         SET NUMBER = NUMBER+1;
        IF(NUMBER < 10) THEN 
          SET PREFIXO = CONCAT("00000",NUMBER); 
        ELSE IF(NUMBER >= 10 AND NUMBER <100) THEN 
          SET PREFIXO = CONCAT("0000",NUMBER); 
        ELSE IF(NUMBER >= 100 AND NUMBER <1000) THEN 
          SET PREFIXO = CONCAT("000",NUMBER); 
        ELSE IF(NUMBER >= 1000 AND NUMBER <10000) THEN 
          SET PREFIXO = CONCAT("00",NUMBER); 
        ELSE IF(NUMBER >= 10000 AND NUMBER <100000) THEN 
          SET PREFIXO = CONCAT("0",NUMBER); 
        ELSE IF(NUMBER >= 100000) THEN 
          SET PREFIXO = NUMBER; 
        END IF;
        END IF;
        END IF;
        END IF;
        END IF;
        END IF;
			 SET REFERENCE = CONCAT(DOCUMENT_SIMBOL,"/",ANO_FATURACAO, "-",PREFIXO);

			 UPDATE AUTO_ORDEM_SERVICO SET
			 DOCUMENT_NUMBER = NUMBER,
			 DOCUMENT_REFERENCE = REFERENCE
			 WHERE ORDER_ID = OS_ID; 

			 UPDATE COM_SERIE_DOCUMENTACAO SET
			 SER_NUMERACAO = SER_NUMERACAO + 1
			 WHERE SER_CODIGO = SERIE;
     END IF;

SELECT OS_ID INTO LSTID;

END
;;
delimiter ;

