DROP PROCEDURE IF EXISTS `stp_AUTO_ORDEM_SERVICO_OBTERPORFILTRO`;
delimiter ;;
CREATE PROCEDURE `stp_AUTO_ORDEM_SERVICO_OBTERPORFILTRO`(IN `FILTER` varchar(5),IN `MATRICULA_ID` varchar(20),IN `CUSTOMER_ID` int,IN `REFERENCE` varchar(12),IN `OS_TYPE` INT(11),IN `OS_STATUS` int,IN `CREATED_BY` varchar(20),IN `TECHNICIAN` int,IN `PRODUCT_ID` int,IN `CHECKIN_FROM` date,IN `CHECKIN_UNTIL` date,IN `BOOKING_FROM` date,IN `BOOKING_UNTIL` date,IN `CHECKOUT_FROM` date,IN `CHECKOUT_UNTIL` date, IN `WORK_FROM` date,IN `WORK_UNTIL` date, IN `CLOSED_FROM` date,IN `CLOSED_UNTIL` date, IN `PREVISION_FROM` date,IN `PREVISION_UNTIL` date, COMPANY_ID INT)
BEGIN
	#Routine body goes here...
DECLARE v_CON_ LONGTEXT;
 
SET v_CON_ = CONCAT("

SELECT ORDER_ID, DOCUMENT_REFERENCE, CUSTOMER.ENT_NOME_COMPLETO AS CUSTOMER, VIA_DESIGNATION, OS_STATUS_ID, BOOK_DATE, CHECKIN_DATE, 
PREDICTION_CLOSING, CHECKOUT_DATE, TECHNICIAN.ENT_NOME_COMPLETO AS TECHNICIAL_RESPONSIBLE, WORK_START_DATE, WORK_FINISH_DATE, OS_TYPE,
(
  SELECT SUM(SUBTOTAL) FROM AUTO_ORDEM_SERVICO_DETAILS 
  INNER JOIN GER_ARTIGO ON ART_CODIGO = ORDER_DETAIL_ID
  WHERE SERVICE_ORDER_ID = ORDER_ID AND ART_TIPO = 'P'
) AS PRODUCTS_SUBTOTAL,

(
  SELECT SUM(SUBTOTAL) FROM AUTO_ORDEM_SERVICO_DETAILS 
  INNER JOIN GER_ARTIGO ON ART_CODIGO = ORDER_DETAIL_ID
  WHERE SERVICE_ORDER_ID = ORDER_ID AND ART_TIPO = 'S'
) AS SERVICES_SUBTOTAL, OW.ENT_NOME_COMPLETO AS PROPRIETARIO, OW.ENT_TELEFONE AS CONTACTO_PROPRIETARIO, CUSTOMER.ENT_TELEFONE AS 
CUSTOMER_CONTACT, INVOICE_ID
 
 
FROM AUTO_ORDEM_SERVICO
INNER JOIN GER_VIATURA V ON V.VIA_CODIGO = CAR_ID
INNER JOIN GER_ENTIDADE AS OW ON OW.ENT_CODIGO = VIA_OWNER_ID
INNER JOIN GER_ENTIDADE AS CUSTOMER ON CUSTOMER.ENT_CODIGO = BILLING_ENTITY_ID
INNER JOIN REST_FUNCIONARIOS ON GAR_CODIGO = TECHNICIAN_ID
INNER JOIN GER_ENTIDADE AS TECHNICIAN ON TECHNICIAN.ENT_CODIGO = GAR_CODIGO_ENTIDADE
LEFT JOIN GER_MARCA MA ON MAR_CODIGO = VIA_MARCA_ID
WHERE  VIA_LICENSE_ID LIKE '%",MATRICULA_ID,"%' AND DOCUMENT_REFERENCE LIKE '%",REFERENCE,"%' AND CREATED_BY LIKE '%",CREATED_BY,"%'
");

IF(COMPANY_ID > 0) THEN
 SET v_CON_ = CONCAT(v_CON_," AND BRANCH_ID =",COMPANY_ID);
END IF;

IF(CUSTOMER_ID > 0) THEN
 SET v_CON_ = CONCAT(v_CON_," OR VIA_OWNER_ID =",CUSTOMER_ID);
END IF;

IF(OS_TYPE IS NOT NULL) THEN
 SET v_CON_=CONCAT(v_CON_," AND OS_TYPE =", OS_TYPE);
END IF;

IF(OS_STATUS > 0) THEN
SET v_CON_=CONCAT(v_CON_," AND OS_STATUS_ID=", OS_STATUS);
END IF;

IF(TECHNICIAN > 0) THEN
SET v_CON_=CONCAT(v_CON_," AND TECHNICIAN_ID=", TECHNICIAN);
END IF;
 IF(CHECKIN_FROM IS NOT NULL  AND CHECKIN_UNTIL IS NOT NULL) THEN
	SET v_CON_ =CONCAT(v_CON_," AND DATE(CHECKIN_DATE) BETWEEN '",CHECKIN_FROM, "' AND '",CHECKIN_UNTIL, "'");
	ELSE IF(CHECKIN_FROM IS NOT NULL AND CHECKIN_UNTIL IS NULL )
	THEN
	SET v_CON_ =CONCAT(v_CON_," AND CHECKIN_DATE >='",CHECKIN_FROM, "'");
	ELSE IF(CHECKIN_FROM IS NULL  AND CHECKIN_UNTIL IS NOT NULL)
	THEN
	SET v_CON_ =CONCAT(v_CON_," AND CHECKIN_DATE <= '",CHECKIN_UNTIL, "'");
	END IF;
	END IF;
	END IF; 

IF(BOOKING_FROM IS NOT NULL  AND BOOKING_UNTIL IS NOT NULL) THEN
	SET v_CON_ =CONCAT(v_CON_," AND  BOOK_DATE BETWEEN '",BOOKING_FROM, "' AND '",BOOKING_UNTIL, "')");
  ELSE IF(BOOKING_FROM IS NOT NULL AND BOOKING_UNTIL IS NULL ) THEN      	
  SET v_CON_ =CONCAT(v_CON_," AND  BOOK_DATE >= '",BOOKING_FROM, "'");
  ELSE IF(BOOKING_FROM IS NULL AND BOOKING_UNTIL IS NOT NULL ) THEN 
	SET v_CON_ =CONCAT(v_CON_," AND  BOOK_DATE <= '",BOOKING_UNTIL, "'");
	END IF;
	END IF;
	END IF; 

IF(CHECKOUT_FROM IS NOT NULL  AND CHECKOUT_UNTIL IS NOT NULL) THEN
	SET v_CON_ =CONCAT(v_CON_," AND  CHECKOUT_DATE BETWEEN  '",CHECKOUT_FROM, "' AND '",CHECKOUT_UNTIL, "'");
	ELSE IF(CHECKOUT_FROM IS NOT NULL AND CHECKOUT_UNTIL IS NULL )
	THEN
	SET v_CON_ =CONCAT(v_CON_," AND  CHECKOUT_DATE >='",CHECKOUT_FROM, "'");
	ELSE IF(CHECKOUT_FROM IS NULL  AND CHECKOUT_UNTIL IS NOT NULL)
	THEN
	SET v_CON_ =CONCAT(v_CON_," AND  CHECKOUT_DATE <= '",CHECKOUT_UNTIL, "'");
	END IF;
	END IF;
	END IF; 

IF(WORK_FROM IS NOT NULL  AND WORK_UNTIL IS NOT NULL) THEN
	SET v_CON_ =CONCAT(v_CON_," AND  WORK_START_DATE BETWEEN  '",WORK_FROM, "' AND '",WORK_UNTIL, "'");
	ELSE IF(WORK_FROM IS NOT NULL AND WORK_UNTIL IS NULL )
	THEN
	SET v_CON_ =CONCAT(v_CON_," AND  WORK_START_DATE >='",WORK_FROM, "'");
	ELSE IF(WORK_FROM IS NULL  AND WORK_UNTIL IS NOT NULL)
	THEN
	SET v_CON_ =CONCAT(v_CON_," AND  WORK_START_DATE <= '",WORK_UNTIL, "'");
	END IF;
	END IF;
	END IF; 

IF(CLOSED_FROM IS NOT NULL  AND CLOSED_UNTIL IS NOT NULL) THEN
	SET v_CON_ =CONCAT(v_CON_," AND  WORK_FINISH_DATE BETWEEN  '",CLOSED_FROM, "' AND '",CLOSED_UNTIL, "'");
	ELSE IF(CLOSED_FROM IS NOT NULL AND CLOSED_UNTIL IS NULL )
	THEN
	SET v_CON_ =CONCAT(v_CON_," AND  WORK_FINISH_DATE >='",CLOSED_FROM, "'");
	ELSE IF(CLOSED_FROM IS NULL  AND CLOSED_UNTIL IS NOT NULL)
	THEN
	SET v_CON_ =CONCAT(v_CON_," AND  WORK_FINISH_DATE <= '",CLOSED_UNTIL, "'");
	END IF;
	END IF;
	END IF; 

IF(PREVISION_FROM IS NOT NULL  AND PREVISION_UNTIL IS NOT NULL) THEN
	SET v_CON_ =CONCAT(v_CON_," AND  PREDICTION_CLOSING BETWEEN  '",PREVISION_FROM, "' AND '",PREVISION_UNTIL, "'");
	ELSE IF(PREVISION_FROM IS NOT NULL AND PREVISION_UNTIL IS NULL )
	THEN
	SET v_CON_ =CONCAT(v_CON_," AND  PREDICTION_CLOSING >='",PREVISION_FROM, "'");
	ELSE IF(PREVISION_FROM IS NULL  AND PREVISION_UNTIL IS NOT NULL)
	THEN
	SET v_CON_ =CONCAT(v_CON_," AND  PREDICTION_CLOSING <='",PREVISION_UNTIL, "'");
	END IF;
	END IF;
	END IF; 


SET @stmt_str = v_CON_;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;


END
;;
delimiter ;

