DROP PROCEDURE IF EXISTS `stp_AUTO_ORDEM_SERVICO_OBTERPORPK`;
delimiter ;;
CREATE PROCEDURE `stp_AUTO_ORDEM_SERVICO_OBTERPORPK`(IN `OS_ID` int)
BEGIN
	#Routine body goes here...
SELECT OS.*, TECHNICIAN.ENT_NOME_COMPLETO AS NOME_TECNICO_RESPONSAVEL, CUSTOMER.ENT_NOME_COMPLETO AS CUSTOMER, 
CI_TECHNICIAN.ENT_NOME_COMPLETO AS  CHECKIN_TECHNICIAN_NAME, CO_TECHNICIAN.ENT_NOME_COMPLETO AS CHECKOUT_TECHNICIAN_NAME,
OW.ENT_NOME_COMPLETO AS PROPRIETARIO, TECHNICIAN.ENT_CODIGO AS TECNICO_RESPONSAVEL_ENTITY_ID, 
CI.GAR_CODIGO_ENTIDADE AS RECEPCIONISTA_ENTITY_ID,  CO.GAR_CODIGO_ENTIDADE AS TECNICO_CHECKOUT_ENTITY_ID
FROM AUTO_ORDEM_SERVICO OS 
LEFT JOIN REST_FUNCIONARIOS R ON R.GAR_CODIGO = TECHNICIAN_ID
LEFT JOIN GER_ENTIDADE AS TECHNICIAN ON TECHNICIAN.ENT_CODIGO = GAR_CODIGO_ENTIDADE
INNER JOIN GER_VIATURA V ON V.VIA_CODIGO = OS.CAR_ID  
INNER JOIN GER_ENTIDADE AS OW ON OW.ENT_CODIGO = VIA_OWNER_ID
INNER JOIN GER_ENTIDADE AS CUSTOMER ON CUSTOMER.ENT_CODIGO = BILLING_ENTITY_ID
LEFT JOIN REST_FUNCIONARIOS CI ON CI.GAR_CODIGO = CHECKIN_TECHNICIAN_ID
LEFT JOIN REST_FUNCIONARIOS CO ON CO.GAR_CODIGO = CHECKOUT_TECHNICIAN_ID
LEFT JOIN GER_ENTIDADE AS CI_TECHNICIAN ON CI_TECHNICIAN.ENT_CODIGO = CI.GAR_CODIGO
LEFT JOIN GER_ENTIDADE AS CO_TECHNICIAN ON CO_TECHNICIAN.ENT_CODIGO = CO.GAR_CODIGO
WHERE ORDER_ID = OS_ID;
END
;;
