CREATE PROCEDURE `stp_CLI_ACOMODACAO_OBTERPORFILTRO`(IN `DESCRICAO` varchar(50), IN `TIPO` INT)
BEGIN
	#Routine body goes here...
SET @ACOMODACAO := CONCAT('%',LOWER(TRIM(DESCRICAO)),'%');

IF(TIPO > 0) THEN
SELECT A.*, C.CAT_DESCRICAO FROM CLI_ACOMODACAO A 
INNER JOIN GER_CATEGORIA C ON C.CAT_CODIGO = A.ACO_CODIGO_TIPO
WHERE C.CAT_SIGLA = 'ACO' AND ACO_DESCRICAO 
LIKE @ACOMODACAO AND A.ACO_CODIGO_TIPO=TIPO order by ACO_DESCRICAO;

ELSE
SELECT A.*, C.CAT_DESCRICAO FROM CLI_ACOMODACAO A 
INNER JOIN GER_CATEGORIA C ON C.CAT_CODIGO = A.ACO_CODIGO_TIPO
WHERE C.CAT_SIGLA = 'ACO' AND ACO_DESCRICAO 
LIKE @ACOMODACAO order by ACO_DESCRICAO;

END IF; 

END
;;
