-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_CLI_ADMISSAO_ACTUALIZAR`;
delimiter ;;
CREATE PROCEDURE `stp_CLI_ADMISSAO_ACTUALIZAR`(IN `CODIGO` INT, 
IN `ESTADO` INT, IN `DESCRICAO` VARCHAR(50), IN `UTILIZADOR` VARCHAR(20), 
IN `ALTERACAO` DATE)
BEGIN
 
 DECLARE STATE INT DEFAULT 0;
 DECLARE SITUACAO INT;
 DECLARE MOTIVO VARCHAR(50);
 DECLARE ACCAO  VARCHAR(10);

 SELECT ATEN_STATUS INTO STATE FROM CLI_ATENDIMENTO WHERE CLI_CODIGO=CODIGO;

 SELECT SIT_CODIGO_STATUS, SIT_DESCRICAO INTO SITUACAO, MOTIVO 
 FROM CLI_ATENDIMENTO_SITUACAO WHERE SIT_CODIGO = STATE;
 
SELECT STA_ACCAO INTO ACCAO FROM CLI_STATUS WHERE STA_CODIGO_STATUS=ESTADO;

 IF(SITUACAO<>ESTADO OR MOTIVO<>DESCRICAO) THEN

	INSERT INTO CLI_ATENDIMENTO_SITUACAO(SIT_CODIGO_ATENDIMENTO, SIT_CODIGO_STATUS,
	 SIT_DATA, SIT_DESCRICAO, SIT_UTILIZADOR) VALUES
	(CODIGO, ESTADO, ALTERACAO, DESCRICAO, UTILIZADOR);
	SELECT LAST_INSERT_ID() INTO ESTADO;
	 
     IF(ACCAO='DELETE') THEN
       UPDATE CLI_ATENDIMENTO_SITUACAO SET SIT_ACCAO='EXCLUSAO' WHERE SIT_CODIGO=ESTADO;
	 ELSE
       UPDATE CLI_ATENDIMENTO_SITUACAO SET SIT_ACCAO='ALTERACAO' WHERE SIT_CODIGO=ESTADO; 
	END IF;
    UPDATE CLI_ATENDIMENTO SET
	ATEN_STATUS = ESTADO WHERE
	ATEN_CODIGO = CODIGO;
 END IF;
END
;;
