delimiter ;;
CREATE PROCEDURE `stp_CLI_ADMISSAO_OBTERPORPK`(IN `CODIGO` INT)
BEGIN
SELECT ATEN_CODIGO, ATEN_CODIGO_SERVICO, ATEN_CODIGO_PROCESSO,ATEN_DATA_REGISTO,
ATEN_DATA_ATENDIMENTO,ATEN_HORA_ATENDIMENTO,ATEN_DATA_TERMINO,ATEN_HORA_TERMINO,
SIT_CODIGO_STATUS, ATEN_CLASSIFICACAO,ATEN_CODIGO_MEDICO,ATEN_CODIGO_UNIDADE,
P.ENT_NOME_COMPLETO AS PACIENTE, P.ENT_DATA_NASCIMENTO, ITEM_DESCRICAO, 
M.ENT_NOME_COMPLETO AS MEDICO, MED_NRO_OMA AS CEDULA, ATEN_STATUS, CODIGO_APOLICE, PES_SEXO
FROM CLI_ATENDIMENTO 

INNER JOIN CLI_SERVICO ON SER_CODIGO = ATEN_CODIGO_SERVICO
INNER JOIN CLI_PACIENTE ON PAC_CODIGO = ATEN_CODIGO_PROCESSO
INNER JOIN CLI_MEDICO ON MED_CODIGO = ATEN_CODIGO_MEDICO
INNER JOIN SIS_EMPRESA ON FIL_CODIGO = ATEN_CODIGO_UNIDADE 
INNER JOIN CLI_ATENDIMENTO_SITUACAO ON SIT_CODIGO = ATEN_STATUS
INNER JOIN GER_STATUS ON STA_CODIGO = SIT_CODIGO_STATUS
LEFT JOIN CLI_ATENDIMENTO_CONVENIO ON CODIGO_ATENDIMENTO = CODIGO
INNER JOIN GER_ENTIDADE P ON P.ENT_CODIGO = PAC_CODIGO
INNER JOIN GER_ENTIDADE M ON M.ENT_CODIGO = MED_CODIGO
LEFT JOIN GER_ENTIDADE_EMPRESA ON EMP_CODIGO = FIL_CODIGO
LEFT JOIN GER_ITEM_COBRANCA ON ITEM_CODIGO = SER_CODIGO
INNER JOIN GER_ENTIDADE_PESSOA PF ON PF.PES_CODIGO = MED_CODIGO
WHERE ATEN_CODIGO = CODIGO;
END
;;
