delimiter ;;
CREATE PROCEDURE `stp_CLI_APOLICE_ADICIONAR`(IN `PACIENTE` int, IN `CONVENIO` int, IN `EMISSAO` date,IN `VALIDADE` date,IN `APOLICE` varchar(20),IN `NUM_PS` varchar(20), IN `TITULAR` bit,IN `ESTADO` bit)
BEGIN
	#Routine body goes here...
DECLARE UTENTE INT DEFAULT 0;
DECLARE SEGURO INT DEFAULT 0;

SELECT PAC_CONV_CODIGO_PACIENTE, PAC_CONV_CODIGO_CONVENIO INTO UTENTE, SEGURO FROM CLI_PACIENTE_CONVENIO
WHERE  PAC_CONV_CODIGO_PACIENTE = PACIENTE AND PAC_CONV_CODIGO_CONVENIO = CONVENIO;

IF(UTENTE >0 AND SEGURO >0) THEN
  UPDATE CLI_PACIENTE_CONVENIO SET
   
  PAC_CONV_APOLICE = APOLICE,
  PAC_CONV_PS = NUM_PS,
  PAC_CONV_INICIO = EMISSAO,
  PAC_CONV_VALIDADE = VALIDADE, 
  PAC_CONV_TITULAR = TITULAR 
  WHERE  PAC_CONV_CODIGO_PACIENTE = PACIENTE 
  AND PAC_CONV_CODIGO_CONVENIO = CONVENIO;
 
 ELSE
  INSERT INTO CLI_PACIENTE_CONVENIO (PAC_CONV_CODIGO_CONVENIO, PAC_CONV_CODIGO_PACIENTE, PAC_CONV_APOLICE, PAC_CONV_PS,
  PAC_CONV_INICIO, PAC_CONV_VALIDADE, PAC_CONV_TITULAR) VALUES (CONVENIO, PACIENTE, APOLICE, NUM_PS, EMISSAO, VALIDADE, TITULAR);
 END IF;
END
;;
