CREATE PROCEDURE `stp_CLI_CONVENIO_OBTERPORFILTRO`(IN `DESCRICAO` varchar(50), IN `SEGURADORA` INT)
BEGIN
	#Routine body goes here...
SET @CONVENIO := CONCAT('%', TRIM(DESCRICAO),'%');

IF(SEGURADORA > 0) THEN
SELECT C.*, ENT_NOME_COMPLETO, TER_NOME_COMERCIAL FROM CLI_PLANO_CONVENIO C 
LEFT JOIN GER_TERCEIROS ON TER_CODIGO = C.CONV_CODIGO_SEGURADORA
LEFT JOIN GER_ENTIDADE E ON ENT_CODIGO = TER_CODIGO_ENTIDADE 
WHERE C.CONV_DESCRICAO LIKE @CONVENIO AND TER_CODIGO=SEGURADORA
ORDER BY C.CONV_DESCRICAO;

ELSE
SELECT C.*, ENT_NOME_COMPLETO, TER_NOME_COMERCIAL FROM CLI_PLANO_CONVENIO C 
LEFT JOIN GER_TERCEIROS ON TER_CODIGO = C.CONV_CODIGO_SEGURADORA
LEFT JOIN GER_ENTIDADE E ON ENT_CODIGO = TER_CODIGO_ENTIDADE 
WHERE C.CONV_DESCRICAO LIKE @CONVENIO ORDER BY C.CONV_DESCRICAO;

END IF; 

END
;;
