CREATE PROCEDURE `stp_CLI_ESCALA_PROFISSIONAL_ADICIONAR`(IN `DIA` int,IN `PROFISSIONAL_ID` int, IN `ESPECIALIDADE_ID` int, IN `T1_INICIO` time, IN `T1_TERMINO` time, IN `T2_INICIO` time, IN `T2_TERMINO` time,
IN `T3_INICIO` time, IN `T3_TERMINO` time, IN `T4_INICIO` time, IN `T4_TERMINO` time, IN STATUS_ID INT, IN UTILIZADOR VARCHAR(20))
BEGIN
	#Routine body goes here...
DECLARE D_SEMANA INT DEFAULT 0;

SELECT PROF_CODIGO INTO PROFISSIONAL_ID FROM CLI_PROFISSIONAIS WHERE PROF_ENTIDADE_ID = PROFISSIONAL_ID;

SELECT ESC_DIA_SEMANA INTO D_SEMANA FROM 
CLI_ESCALA_PROFISSIONAL WHERE ESC_DIA_SEMANA=DIA AND ESC_CODIGO_PROFISSIONAL=PROFISSIONAL_ID;

IF(D_SEMANA<=0 OR D_SEMANA IS NULL)THEN
INSERT INTO CLI_ESCALA_PROFISSIONAL (ESC_DIA_SEMANA,
ESC_CODIGO_PROFISSIONAL,
ESC_PERIODO1_INICIO,
ESC_PERIODO1_TERMINO,
ESC_PERIODO2_INICIO,
ESC_PERIODO2_TERMINO,
ESC_PERIODO3_INICIO,
ESC_PERIODO3_TERMINO,
ESC_PERIODO4_INICIO,
ESC_PERIODO4_TERMINO,
ESC_STATUS,
ESC_CREATED_BY,
ESC_CREATED_DATE,
ESC_UPDATED_BY,
ESC_UPDATED_DATE 
)
VALUES(DIA, PROFISSIONAL_ID, T1_INICIO, T1_TERMINO, T2_INICIO, T2_TERMINO, T3_INICIO, T3_TERMINO, T4_INICIO, T4_TERMINO, STATUS_ID, UTILIZADOR, NOW(), UTILIZADOR, NOW());
ELSE
UPDATE CLI_ESCALA_PROFISSIONAL SET   
ESC_PERIODO1_INICIO = T1_INICIO,
ESC_PERIODO1_TERMINO = T1_TERMINO,
ESC_PERIODO2_INICIO = T2_INICIO,
ESC_PERIODO2_TERMINO = T2_TERMINO,
ESC_PERIODO3_INICIO = T3_INICIO,
ESC_PERIODO3_TERMINO = T3_TERMINO,
ESC_PERIODO4_INICIO = T4_INICIO,
ESC_PERIODO4_TERMINO = T4_TERMINO,
ESC_STATUS = STATUS_ID,
ESC_UPDATED_BY = UTILIZADOR,
ESC_UPDATED_DATE = NOW()
WHERE ESC_DIA_SEMANA=DIA AND ESC_CODIGO_PROFISSIONAL=PROFISSIONAL_ID;
END IF;
END
;;
