-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_CLI_ESCALA_PROFISSIONAL_OBTERPORFILTRO`;
delimiter ;;
CREATE PROCEDURE `stp_CLI_ESCALA_PROFISSIONAL_OBTERPORFILTRO`(IN `CODIGO` int, IN `PROFISSIONAL_ID` int, IN `ESPECIALIDADE_ID` int, IN `DIA` DATE)
BEGIN
	#Routine body goes here...
DECLARE vCONSULTA LONGTEXT;

IF(PROFISSIONAL_ID > 0) THEN
	SELECT PROF_CODIGO INTO PROFISSIONAL_ID FROM CLI_PROFISSIONAIS WHERE PROF_ENTIDADE_ID = PROFISSIONAL_ID;
END IF;

SET vCONSULTA = CONCAT("
SELECT DISTINCT
ESC_CODIGO,
ESC_DIA_SEMANA,
ESC_CODIGO_PROFISSIONAL,
ESC_PERIODO1_INICIO,
ESC_PERIODO1_TERMINO,
ESC_PERIODO2_INICIO,
ESC_PERIODO2_TERMINO,
ESC_PERIODO3_INICIO,
ESC_PERIODO3_TERMINO,
ESC_PERIODO4_INICIO,
ESC_PERIODO4_TERMINO,
ESC_STATUS,
ESC_CREATED_BY,
ESC_CREATED_DATE,
ESC_UPDATED_BY,
ESC_UPDATED_DATE,
ENT_NOME_COMPLETO, 
CONCAT(CAT_PROF_TRATAMENTO, '', ENT_NOME_COMPLETO) AS TRATAMENTO,
ENT_CODIGO
FROM CLI_ESCALA_PROFISSIONAL
LEFT JOIN CLI_PROFISSIONAL_ESPECIALIDADES ON PROF_ESP_PROFISSIONAL_ID = ESC_CODIGO_PROFISSIONALLEFT JOIN CLI_ESPECIALIDADE ON ESP_CODIGO = PROF_ESP_ESPECIALIDADE_ID
INNER JOIN CLI_PROFISSIONAIS ON PROF_CODIGO = ESC_CODIGO_PROFISSIONAL
INNER JOIN GER_ENTIDADE ON ENT_CODIGO = PROF_ENTIDADE_ID 
INNER JOIN CLI_CATEGORIA_PROFISSIONAL ON CAT_PROF_CODIGO = PROF_CATEGORIA_PROFISSIONAL

WHERE 1=1 ");
IF(CODIGO > 0) THEN
  SET vCONSULTA = CONCAT(vCONSULTA," AND ESC_CODIGO = ", CODIGO);
ELSE

	IF(PROFISSIONAL_ID > 0) THEN
		 SET vCONSULTA = CONCAT(vCONSULTA, " AND ESC_CODIGO_PROFISSIONAL = ", PROFISSIONAL_ID); 
	END IF; 
	
	IF(ESPECIALIDADE_ID > 0) THEN
		 SET vCONSULTA = CONCAT(vCONSULTA, " AND PROF_ESP_ESPECIALIDADE_ID = ", ESPECIALIDADE_ID); 
	END IF;
	
	IF(DIA IS NOT NULL) THEN
		 SET vCONSULTA = CONCAT(vCONSULTA, " AND ESC_DIA_SEMANA = ", DAYOFWEEK(DIA)); 
	END IF;
	
END IF;
SET vCONSULTA = CONCAT(vCONSULTA," ORDER BY ESC_DIA_SEMANA");
SET @stmt_str = vCONSULTA;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt; 
 
END
;;
delimiter ;

