BEGIN
	#Routine body goes here...
 
DECLARE ACCAO VARCHAR(20);

IF(CATEGORIA =-1) THEN
SET CATEGORIA = NULL;
END IF;
IF(CODIGO <=0) THEN
IF(NOT EXISTS(SELECT * FROM GER_CATEGORIA WHERE CAT_DESCRICAO = DESCRICAO AND CAT_CATEGORIA = CATEGORIA))THEN
 INSERT INTO GER_CATEGORIA (CAT_DESCRICAO, CAT_SIGLA, CAT_STATUS, CAT_CATEGORIA, CAT_CODIGO_FILIAL)
 VALUES(DESCRICAO, SIGLA, 1, CATEGORIA, FILIAL);
 SET ACCAO="INCLUSAO"; 
 SELECT LAST_INSERT_ID() INTO CODIGO;
 INSERT INTO CLI_LABORATORIO_CATEGORIA_EXAME VALUES(CODIGO, CATEGORIA);		

ELSE
SET LSTID = -1;
END IF;

ELSE
UPDATE GER_CATEGORIA SET
CAT_DESCRICAO = DESCRICAO,
CAT_SIGLA = SIGLA,
#CAT_STATUS = SITUACAO,
CAT_CATEGORIA = CATEGORIA,
CAT_CODIGO_FILIAL = FILIAL
WHERE CAT_CODIGO = CODIGO;
UPDATE CLI_LABORATORIO_CATEGORIA_EXAME SET CAT_EXAME_CODIGO_PARENT_ID = CATEGORIA 
WHERE CAT_EXAME_CODIGO = CODIGO;
SET ACCAO ="ALTERACAO"; 
END IF;

SET LSTID =CODIGO;

SET DESCRICAO=CONCAT( ACCAO, " TABELA DE CATEGORIAS - ",DESCRICAO," Nº ", CODIGO);

INSERT INTO SIS_LOGS(LOG_UTILIZADOR, LOG_FORMULARIO, LOG_ACCAO, LOG_IP, LOG_ALTERACAO, LOG_DATA) VALUES
(UTILIZADOR, "TABELA DE CATEGORIAS", ACCAO, "", DESCRICAO, NOW());


END
;;
