delimiter ;;
CREATE PROCEDURE `stp_CLI_LABORATORIO_CATEGORIA_EXAME_OBTERPORFILTRO`(IN `DESCRICAO` varchar(120),IN `SIGLA` varchar(10),IN `CATEGORIA` int, IN FILIAL INT)
BEGIN
	#Routine body goes here...
DECLARE vCONSULTA LONGTEXT;

SET vCONSULTA =CONCAT("
SELECT C.CAT_CODIGO, C.CAT_DESCRICAO, C.CAT_SIGLA, C.CAT_STATUS, TIP_DESCRICAO, CAT_CODIGO_FILIAL FROM GER_CATEGORIA C
INNER JOIN CLI_LABORATORIO_CATEGORIA_EXAME ON CAT_EXAME_CODIGO = CAT_CODIGO
LEFT JOIN GER_TIPO  ON TIP_CODIGO = CAT_CATEGORIA
WHERE CAT_STATUS IN('A','1') AND CAT_DESCRICAO LIKE '%",DESCRICAO,"%'");  

IF(SIGLA IS NOT NULL AND SIGLA !='') THEN
 SET vcONSULTA =CONCAT(vCONSULTA, " AND CAT_SIGLA ='",SIGLA,"'");
END IF;

IF(CATEGORIA > 0) THEN
 SET vCONSULTA = CONCAT(vCONSULTA, " AND CAT_CATEGORIA = ", CATEGORIA);
ELSE
SET vCONSULTA = CONCAT(vCONSULTA, " AND CAT_CATEGORIA IS NULL ");
END IF;

IF((CATEGORIA <=0 OR CATEGORIA IS NULL) AND FILIAL > 0) THEN
 SET vCONSULTA = CONCAT(vCONSULTA, " AND (CAT_CODIGO_FILIAL IS NULL OR  CAT_CODIGO_FILIAL =" ,FILIAL, ")"); 
END IF;
SET vCONSULTA = CONCAT(vCONSULTA, "  ORDER BY CAT_DESCRICAO");

SET @stmt_str = vCONSULTA;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;
END
;;
delimiter ;

