CREATE PROCEDURE `stp_CLI_LABORATORIO_EXAME_OBTERPORFILTRO`(IN `DESCRICAO` varchar(120),IN `CATEGORIA` int,IN `AMOSTRA_ID` int)
BEGIN
	#Routine body goes here...
 DECLARE vCONSULTA LONGTEXT;
 SET vCONSULTA = CONCAT("
 SELECT EXA_CODIGO, EXA_DESCRICAO, EXA_SIGLA, CAT_DESCRICAO, AMO_DESCRICAO, EXA_STATUS, UNI_SIGLA,    ART_CODIGO_FILIAL   FROM CLI_LABORATORIO_EXAME 
 INNER JOIN GER_ARTIGO ON ART_CODIGO = EXA_CODIGO_ARTIGO
 INNER JOIN GER_CATEGORIA ON CAT_CODIGO = EXA_CATEGORIA_ID
 LEFT JOIN CLI_LABORATORIO_AMOSTRA ON AMO_CODIGO = EXA_CODIGO_AMOSTRA
 INNER JOIN GER_UNIDADE_MEDICAO ON UNI_CODIGO = EXA_UNIDADE
 WHERE (EXA_DESCRICAO LIKE '%",DESCRICAO,"%' OR EXA_SIGLA LIKE '%",DESCRICAO,"%')
 ");
 IF(CATEGORIA > 0) THEN 
    SET vCONSULTA = CONCAT(vCONSULTA," AND (ART_CATEGORIA IN (SELECT CAT_CODIGO FROM GER_CATEGORIA WHERE CAT_CATEGORIA = ", CATEGORIA, ") OR ART_CATEGORIA =", CATEGORIA," )"); 
	END IF;
	
 IF(AMOSTRA_ID > 0) THEN
   SET vCONSULTA = CONCAT(vCONSULTA," AND EXA_CODIGO_AMOSTRA =", AMOSTRA_ID);
 END IF;
 
 
 SET vCONSULTA = CONCAT(vCONSULTA," ORDER BY ART_CODIGO ");
 SET @stmt_str = vCONSULTA;
 PREPARE stmt FROM @stmt_str;
 EXECUTE stmt;
 DEALLOCATE PREPARE stmt;

END
;;
delimiter ;

