BEGIN
	#Routine body goes here...
DECLARE vCONSULTA LONGTEXT;

SET vCONSULTA = CONCAT("
SELECT REQ_CODIGO, REQ_PACIENT_ID, REQ_REQUESTER_ID, REQ_REQUEST_DATE, REQ_STATUS, REQ_DELIVERY_PREVISION, REQ_DELIVERY_DATE,
U.ENT_NOME_COMPLETO AS NOME_PACIENTE, P.ENT_NOME_COMPLETO AS NOME_PROFISSIONAL, REQ_PRIORITY, (SELECT COUNT(*) FROM cli_laboratorio_requisicao_exame_detalhes WHERE REQUEST_DETAIL_REQUEST_ID = REQ_CODIGO) AS TOTAL_EXAMES,
