FROM CLI_LABORATORIO_REQUISICAO_EXAME R
INNER JOIN CLI_UTENTE ON UTE_CODIGO = REQ_PACIENT_ID
INNER JOIN GER_ENTIDADE U ON U.ENT_CODIGO = UTE_CODIGO_ENTIDADE
LEFT JOIN CLI_PROFISSIONAIS ON PROF_CODIGO = R.REQ_REQUESTER_ID
LEFT JOIN GER_ENTIDADE P ON P.ENT_CODIGO = PROF_ENTIDADE_ID 
WHERE 1=1");

