END IF;

IF(REQUESTED_FROM IS NOT NULL  AND REQUESTED_UNTIL IS NOT NULL) THEN
		 SET vCONSULTA =CONCAT(vCONSULTA," AND DATE(REQ_REQUEST_DATE) BETWEEN  '",REQUESTED_FROM, "' AND '",REQUESTED_UNTIL, "'"); 
END IF;  
END IF;


#SELECT vCONSULTA;

SET vCONSULTA = CONCAT(vCONSULTA," ORDER BY REQ_REQUEST_DATE ");
SET @stmt_str = vCONSULTA;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;

END
;;
