delimiter ;;
CREATE PROCEDURE `stp_CLI_MARCACAO_OBTERPORFILTRO`(IN `PROFISSIONAL_ID` int,IN `ESPECIALIDADE_ID` int,IN `PACIENTE_ID` int,IN `HORA` time,IN `DATA_INI` date, IN `DATA_TERM` date,IN `STATUS_ID` int, IN `UTILIZADOR` varchar(20), IN `FILIAL` int)
BEGIN
	#Routine body goes here...
	
SELECT ATEN_CODIGO, UTE.ENT_CODIGO, CONCAT(UTE_CODIGO,' - ',UTE.ENT_NOME_COMPLETO) AS PACIENTE, ATEN_CODIGO_UNIDADE, 
ATEN_CODIGO_ESPECIALIDADE, ESP_DESCRICAO, ATEN_CODIGO_PROFISSIONAL_ID, M.ENT_NOME_COMPLETO AS NOME_PROFISSIONAL, 
ATEN_SCHEDULE_DATE, ATEN_SCHEDULE_TIME,  ATEN_SCHEDULE_STATUS, STA_DESCRICAO
FROM CLI_ATENDIMENTO  
INNER JOIN CLI_UTENTE ON UTE_CODIGO = ATEN_CODIGO_UTENTE
INNER  JOIN CLI_PROFISSIONAIS ON PROF_CODIGO = ATEN_CODIGO_PROFISSIONAL_ID
LEFT JOIN SIS_EMPRESA ON FIL_CODIGO = ATEN_CODIGO_UNIDADE
LEFT JOIN GER_ENTIDADE_PESSOA PROF_SAU ON PROF_SAU.PES_CODIGO = PROF_ENTIDADE_ID  
LEFT JOIN GER_ENTIDADE UTE ON UTE.ENT_CODIGO = UTE_CODIGO_ENTIDADE
LEFT JOIN CLI_PLANO_CONVENIO ON CONV_CODIGO = ATEN_CONVENIO_ID  
INNER JOIN CLI_ESPECIALIDADE ON ESP_CODIGO = ATEN_CODIGO_ESPECIALIDADE
INNER JOIN GER_ENTIDADE M ON M.ENT_CODIGO = PROF_ENTIDADE_ID
INNER JOIN CLI_MARCACAO_STATUS ON STA_CODIGO = ATEN_SCHEDULE_STATUS;
END
;;
