CREATE PROCEDURE `stp_CLI_PACIENTE_ADICIONAR`(IN `CODIGO` integer, IN `NOME_COMPLETO` VARCHAR(120), IN `DATA_NASCIMENTO` date, IN `LOCAL_NASCIMENTO` integer, IN `NACIONALIDADE` integer, IN `PAIS` integer, 
IN `LOCAL_MORADA` INT, IN `RUA` varchar(80), IN `BAIRRO` varchar(80), IN `TELEFONE` varchar(18), IN `TELF_ALT` varchar(18), IN `FAX` varchar(18), IN `EMAIL` varchar(120),  IN `SEXO` varchar(1), IN `CIVIL` varchar(1), IN `PAI` VARCHAR(45), IN `MAE` VARCHAR(45), IN `ESTADO` bit, IN `FOTOGRAFIA` longblob, IN `EXTENSAO` varchar(5), IN `PATH` varchar(255),  IN `DOCUMENTO` integer, IN `IDENTIFICACAO` varchar(18), IN `GRUPO` int, IN `RACA` int , IN `RELIGIAO` int, IN `FILIAL` INT, IN `NATURALIDADE` INT, IN `SEGURADORA` INT, IN `CONVENIO` INT, IN `APOLICE` VARCHAR(12), IN `VALIDADE` DATE, IN `UTILIZADOR` varchar(20), IN `DEFICIENCIA` INT, IN DISTRITO VARCHAR(80), OUT `LSTID` INT)
BEGIN

DECLARE DOCUMENTACAO INT DEFAULT 0;

SELECT ED.ENT_DOC_CODIGO INTO DOCUMENTACAO FROM GER_ENTIDADE_DOCUMENTACAO ED 
WHERE ENT_DOC_CODIGO_DOCUMENTO=DOCUMENTO AND ENT_DOC_NUMERO =IDENTIFICACAO;

IF DOCUMENTACAO = 0 THEN
	#Routine body goes here...
INSERT INTO GER_ENTIDADE (ENT_NOME_COMPLETO, ENT_DATA_NASCIMENTO, ENT_LOCAL_NASCIMENTO, ENT_CODIGO_PAIS, ENT_LOCAL_MORADA, ENT_RUA, ENT_BAIRRO,
ENT_TELEFONE, ENT_TELEFONE_ALTERNATIVO, ENT_TELEFONE_FAX, ENT_EMAIL, ENT_STATUS, ENT_DATA_INCLUSAO, ENT_IDENTIFICACAO, ENT_NACIONALIDADE, ENT_FOTOGRAFIA,
ENT_FOTOGRAFIA_PATH, ENT_FOTOGRAFIA_EXTENSAO, ENT_DISTRITO, ENT_CREATED_BY, ENT_UPDATED_BY, ENT_UPDATED_DATE) VALUES 
(NOME_COMPLETO, DATA_NASCIMENTO, LOCAL_NASCIMENTO, NACIONALIDADE, LOCAL_MORADA, RUA, BAIRRO, TELEFONE, TELF_ALT, FAX, EMAIL, 1, CURDATE(),
IDENTIFICACAO, NACIONALIDADE, FOTOGRAFIA, PATH, EXTENSAO, DISTRITO, UTILIZADOR, UTILIZADOR, NOW());
SELECT LAST_INSERT_ID() INTO CODIGO;

INSERT INTO GER_ENTIDADE_PESSOA(PES_CODIGO, PES_SEXO, PES_ESTADO_CIVIL, 
PES_CODIGO_GRUPO_SANGUINEO, PES_NOME_PAI, PES_NOME_MAE, PES_NATURALIDADE, PES_RACA, PES_RELIGIAO) 
VALUES(CODIGO, SEXO, CIVIL, GRUPO, PAI, MAE, NATURALIDADE,  RACA, RELIGIAO);

INSERT INTO CLI_UTENTE (UTE_CODIGO_ENTIDADE, UTE_STATUS,
UTE_CREATED_BY, UTE_CREATED_DATE, UTE_UPDATE_BY, UTE_UPDATED_DATE) 
VALUES (CODIGO, 1, UTILIZADOR, NOW(), UTILIZADOR, NOW()); 


INSERT INTO GER_ENTIDADE_DOCUMENTACAO (ENT_DOC_CODIGO_ENTIDADE, ENT_DOC_CODIGO_DOCUMENTO, ENT_DOC_NUMERO) VALUES(CODIGO, DOCUMENTO, IDENTIFICACAO);
SELECT LAST_INSERT_ID() INTO DOCUMENTACAO;

UPDATE GER_ENTIDADE SET 
ENT_IDENTIFICACAO = IDENTIFICACAO
WHERE ENT_CODIGO = CODIGO;

INSERT INTO GER_TERCEIROS (TER_CODIGO_ENTIDADE, TER_CODIGO_CATEGORIA, TER_TIPO, TER_NOME_COMERCIAL)
VALUES(CODIGO, 1, 'C', NOME_COMPLETO);


SET LSTID:=CODIGO;
ELSE
SET LSTID:=-2;
END IF;
END
;;
