DROP PROCEDURE IF EXISTS `stp_CLI_PACIENTE_ALTERAR`;
delimiter ;;
CREATE PROCEDURE `stp_CLI_PACIENTE_ALTERAR`(IN CODIGO INT, IN NOME_COMPLETO VARCHAR(120), DATA_NASCIMENTO DATE, LOCAL_NASCIMENTO INT, NACIONALIDADE INT, PAIS INT, LOCAL_MORADA INT, RUA VARCHAR(120), BAIRRO VARCHAR(120), TELEFONE VARCHAR(18), TELF_ALT VARCHAR(18),  FAX VARCHAR(18), EMAIL VARCHAR(80) , SEXO VARCHAR(1) , CIVIL VARCHAR(1) , PAI VARCHAR(120), MAE VARCHAR(120) , ESTADO VARCHAR(1), FOTOGRAFIA longblob, EXTENSAO VARCHAR(10), PATH VARCHAR(255), IN `DOCUMENTO` integer, IN `IDENTIFICACAO` varchar(18), IN `GRUPO` int, IN `RACA` int , IN `RELIGIAO` int, IN `FILIAL` INT, IN `NATURALIDADE` INT, IN `SEGURADORA` INT, IN `CONVENIO` INT, IN `APOLICE` VARCHAR(12), IN `VALIDADE` DATE, IN `UTILIZADOR`VARCHAR(20), IN `DEFICIENCIA` INT, IN DISTRITO VARCHAR(80))
BEGIN
	#Routine body goes here...
DECLARE DOCUMENTACAO INT DEFAULT 0;
DECLARE PROCESSO INT DEFAULT 0;
DECLARE ENTIDADE INT DEFAULT 0;
DECLARE BI VARCHAR(18);
DECLARE SITUACAO BIT DEFAULT 1;
SELECT ENT_IDENTIFICACAO INTO  BI FROM GER_ENTIDADE WHERE ENT_CODIGO = CODIGO;

IF(BI<>IDENTIFICACAO) THEN
 SELECT ENT_CODIGO INTO ENTIDADE FROM GER_ENTIDADE WHERE ENT_IDENTIFICACAO = IDENTIFICACAO;
END IF;

IF(ESTADO != 'A') THEN
  SET SITUACAO = 0;
END IF;

IF(ENTIDADE =0) THEN
UPDATE GER_ENTIDADE SET 
ENT_NOME_COMPLETO = NOME_COMPLETO,
ENT_DATA_NASCIMENTO=DATA_NASCIMENTO,
ENT_LOCAL_NASCIMENTO =LOCAL_NASCIMENTO, 
ENT_CODIGO_PAIS= PAIS, 
ENT_LOCAL_MORADA = LOCAL_MORADA, 
ENT_RUA = RUA, ENT_BAIRRO=BAIRRO,
ENT_TELEFONE=TELEFONE,
ENT_TELEFONE_ALTERNATIVO=TELF_ALT, 
ENT_TELEFONE_FAX=FAX, ENT_EMAIL=EMAIL, ENT_STATUS=SITUACAO, 
ENT_DISTRITO = DISTRITO, ENT_UPDATED_BY = UTILIZADOR, ENT_UPDATED_DATE = NOW(),
ENT_FOTOGRAFIA = FOTOGRAFIA, ENT_FOTOGRAFIA_EXTENSAO = EXTENSAO, ENT_FOTOGRAFIA_PATH = PATH,
ENT_NACIONALIDADE = NACIONALIDADE

WHERE ENT_CODIGO = CODIGO;

UPDATE GER_ENTIDADE_PESSOA SET
PES_SEXO = SEXO, PES_ESTADO_CIVIL = CIVIL,
PES_CODIGO_GRUPO_SANGUINEO = GRUPO,
PES_NOME_PAI = PAI, PES_NOME_MAE=MAE, PES_NATURALIDADE = NATURALIDADE,
PES_RACA = RACA, PES_RELIGIAO = RELIGIAO
WHERE PES_CODIGO = CODIGO;

UPDATE CLI_UTENTE SET 
UTE_CODIGO_DEFICIENCIA = DEFICIENCIA,
UTE_UPDATE_BY = UTILIZADOR,
UTE_UPDATED_DATE = NOW()
WHERE UTE_CODIGO_ENTIDADE = CODIGO;  

SELECT ENT_DOC_CODIGO INTO DOCUMENTACAO FROM GER_ENTIDADE_DOCUMENTACAO
WHERE ENT_DOC_NUMERO = IDENTIFICACAO;

IF DOCUMENTACAO = 0 THEN
 INSERT INTO GER_ENTIDADE_DOCUMENTACAO (ENT_DOC_CODIGO_ENTIDADE, ENT_DOC_CODIGO_DOCUMENTO, ENT_DOC_NUMERO) 
 VALUES(CODIGO, DOCUMENTO, IDENTIFICACAO);
 SELECT LAST_INSERT_ID() INTO DOCUMENTACAO;
ELSE
  
  UPDATE GER_ENTIDADE_DOCUMENTACAO SET
  ENT_DOC_CODIGO_ENTIDADE=CODIGO, ENT_DOC_CODIGO_DOCUMENTO=DOCUMENTO, 
  ENT_DOC_NUMERO=IDENTIFICACAO
  WHERE ENT_DOC_CODIGO = DOCUMENTACAO;
END IF;

IF(DOCUMENTO >0) THEN
 UPDATE GER_ENTIDADE SET
 ENT_IDENTIFICACAO = IDENTIFICACAO
 WHERE ENT_CODIGO = CODIGO;
END IF;
IF(NOT EXISTS(SELECT * FROM GER_TERCEIROS WHERE TER_CODIGO_ENTIDADE = CODIGO)) THEN
	INSERT INTO GER_TERCEIROS (TER_CODIGO_ENTIDADE, TER_CODIGO_CATEGORIA, TER_TIPO, TER_NOME_COMERCIAL) 
  VALUES(CODIGO, 1, 'C', NOME_COMPLETO);  
END IF;
END IF; 
END
;;
