-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_CLI_PACIENTE_OBTERPORFILTRO`;
delimiter ;;
CREATE PROCEDURE `stp_CLI_PACIENTE_OBTERPORFILTRO`(IN `NOME` varchar(120), IN `DATA_NASCIMENTO` DATE)
BEGIN
	#Routine body goes here...
	DECLARE vCONSULTA LONGTEXT;

SET vCONSULTA = CONCAT("
SELECT E.ENT_CODIGO, ENT_DATA_INCLUSAO, E.ENT_NOME_COMPLETO, E.ENT_DATA_NASCIMENTO,  CASE PES_SEXO
WHEN 'M' THEN 'MASCULINO'
WHEN 'F' THEN 'FEMININO' 
END AS PES_SEXO, CASE PES_ESTADO_CIVIL
WHEN 'S' THEN 'SOLTEIRO(A)'
WHEN 'C' THEN 'CASADO(A)'
WHEN 'V' THEN 'VIUVO(A)' 
WHEN 'D' THEN 'DIVORCIADO(A)' 
END AS PES_ESTADO_CIVIL, 
E.ENT_IDENTIFICACAO, E.ENT_TELEFONE, E.ENT_TELEFONE_ALTERNATIVO, ENT_EMAIL, PES_FOTO_PATH, UTE_STATUS, UTE_CODIGO FROM GER_ENTIDADE E
INNER JOIN GER_ENTIDADE_PESSOA PF ON PF.PES_CODIGO = E.ENT_CODIGO
INNER JOIN CLI_UTENTE PC ON PC.UTE_CODIGO_ENTIDADE = PF.PES_CODIGO  
WHERE ENT_NOME_COMPLETO LIKE '%",LOWER(TRIM(NOME)),"%'  OR ENT_TELEFONE LIKE '%",LOWER(TRIM(NOME)),"%' OR ENT_IDENTIFICACAO LIKE '%",LOWER(TRIM(NOME)), "%' OR  ENT_EMAIL LIKE '%",LOWER(TRIM(NOME)),"%' OR ENT_TELEFONE LIKE '%",LOWER(TRIM(NOME)),"%'");

SET vCONSULTA = CONCAT(vCONSULTA," ORDER BY ENT_NOME_COMPLETO ");

#SELECT vCONSULTA;

SET @stmt_str = vCONSULTA;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;

END
;;
