delimiter ;;
CREATE PROCEDURE `stp_CLI_PACIENTE_OBTERPORPK`(IN `CODIGO` int)
BEGIN
	#Routine body goes here...
SELECT E.*, PF.*, UTE_CODIGO, "-1" AS UTE_CODIGO_DEFICIENCIA, DC.ENT_DOC_CODIGO_DOCUMENTO, PROV_CODIGO, CONV_CODIGO_SEGURADORA, PAC_CONV_CODIGO_PLANO, pac_conv_apolice, pac_conv_VALIDADE  FROM GER_ENTIDADE E
INNER JOIN GER_ENTIDADE_PESSOA PF ON PF.PES_CODIGO = E.ENT_CODIGO
INNER JOIN CLI_UTENTE PC ON UTE_CODIGO_ENTIDADE = PF.PES_CODIGO
LEFT JOIN GER_ENTIDADE_DOCUMENTACAO DC ON DC.ENT_DOC_CODIGO_ENTIDADE = ENT_CODIGO
LEFT JOIN GER_MUNICIPIO ON MUN_CODIGO = ENT_LOCAL_MORADA
LEFT JOIN GER_PROVINCIA ON PROV_CODIGO = MUN_CODIGO_PROVINCIA
LEFT JOIN CLI_PACIENTE_CONVENIO ON PAC_CONV_CODIGO_PACIENTE  = UTE_CODIGO
LEFT JOIN CLI_PLANO_CONVENIO C ON C.conv_codigo = PAC_CONV_CODIGO_PLANO 
WHERE UTE_CODIGO = CODIGO;
END
;;
