-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_CLI_PROFISSIONAL_ADICIONAR`;
delimiter ;;
CREATE PROCEDURE `stp_CLI_PROFISSIONAL_ADICIONAR`(IN `CODIGO` integer, IN`NOME_COMPLETO` VARCHAR(80), IN `DATA_NASCIMENTO` date,IN `LOCAL_NASCIMENTO` integer,IN `NACIONALIDADE` integer,IN `IDENTIFICACAO`varchar(18),IN `LOCAL_MORADA` integer,IN `RUA` varchar(50),IN `BAIRRO` varchar(50),IN `TELEFONE` varchar(18),IN `TELF_ALT` varchar(18),IN `FAX` varchar(18),IN `EMAIL` varchar(40), IN `SEXO` varchar(1), IN `ESTADO_CIVIL` varchar(1), IN `DOCUMENTO` integer, IN `NATURALIDADE` INT, PAIS INT, ESPECIALIDADE INT, IN `NUM_ORDEM` VARCHAR(10), IN `FOTOGRAFIA` longblob, IN `EXTENSAO` varchar(5),IN `PATH` varchar(150), UTILIZADOR VARCHAR(20), SENHA LONGTEXT, IN PROFISSAO INT, IN FILIAL INT, OUT `LSTID` INT)
BEGIN

DECLARE DOCUMENTACAO INT DEFAULT 0;
DECLARE PROFISSIONAL_ID INT DEFAULT 0;

SELECT IFNULL(PROF_ENTIDADE_ID, 0) INTO PROFISSIONAL_ID FROM CLI_PROFISSIONAIS WHERE PROF_NRO_ORDEM=NUM_ORDEM AND
PROF_CATEGORIA_PROFISSIONAL = PROFISSAO;

SELECT ED.ENT_DOC_CODIGO INTO DOCUMENTACAO FROM GER_ENTIDADE_DOCUMENTACAO ED 
WHERE ENT_DOC_CODIGO_DOCUMENTO=DOCUMENTO AND ENT_DOC_CODIGO_ENTIDADE=CODIGO
AND ENT_DOC_NUMERO =IDENTIFICACAO;
 
IF (PROFISSIONAL_ID = 0 AND DOCUMENTACAO = 0) THEN
	#Routine body goes here...
INSERT INTO GER_ENTIDADE (ENT_NOME_COMPLETO, ENT_DATA_NASCIMENTO, ENT_LOCAL_NASCIMENTO, ENT_NACIONALIDADE, ENT_CODIGO_PAIS, ENT_LOCAL_MORADA, ENT_RUA, ENT_BAIRRO,
ENT_TELEFONE, ENT_TELEFONE_ALTERNATIVO, ENT_TELEFONE_FAX, ENT_EMAIL, ENT_STATUS, ENT_DATA_INCLUSAO) VALUES 
(NOME_COMPLETO, DATA_NASCIMENTO, LOCAL_NASCIMENTO, NACIONALIDADE, PAIS, LOCAL_MORADA, RUA, BAIRRO, TELEFONE, TELF_ALT, FAX, EMAIL, 1, CURDATE());
SELECT LAST_INSERT_ID() INTO CODIGO;
SET LSTID:=CODIGO;

INSERT INTO GER_ENTIDADE_PESSOA(PES_CODIGO, PES_NOME, PES_SOBRENOME, PES_SEXO, PES_ESTADO_CIVIL, PES_FOTOGRAFIA, 
PES_FOTO_EXTENSAO, PES_FOTO_PATH) 
VALUES(CODIGO, NOME_COMPLETO, NOME_COMPLETO, SEXO, ESTADO_CIVIL, FOTOGRAFIA, EXTENSAO, PATH); 

INSERT INTO CLI_PROFISSIONAIS (
PROF_ENTIDADE_ID,
PROF_CATEGORIA_PROFISSIONAL,
PROF_NRO_ORDEM,
PROF_ESPECIALIDADE_CLINCIA_ID,
PROF_CREATED_BY,
PROF_CREATED_DATE,
PROF_UPDATED_BY,
PROF_UPDATED_DATE,
PROF_FILIAL_ID
) 
VALUES (CODIGO, PROFISSAO, NUM_ORDEM, ESPECIALIDADE, UTILIZADOR, NOW(), UTILIZADOR, NOW(), FILIAL); 

IF(ESPECIALIDADE > 0) THEN
 CALL stp_CLI_PROFISSIONAL_ESPECIALIDADE_ADICIONAR(CODIGO, ESPECIALIDADE, UTILIZADOR, 1);
 UPDATE CLI_PROFISSIONAL_ESPECIALIDADES SET 
 ESPECIALIDADE_PRINCIPAL = 1
 WHERE PROFISSIONAL_ID = CODIGO AND 
ESPECIALIDADE_ID = ESPECIALIDADE_ID;

END IF;

INSERT INTO GER_ENTIDADE_DOCUMENTACAO (ENT_DOC_CODIGO_ENTIDADE, ENT_DOC_CODIGO_DOCUMENTO, ENT_DOC_NUMERO,
ENT_DOC_EMISSAO, ENT_DOC_VALIDADE, ENT_DOC_LOCAL_EMISSAO) 
VALUES(CODIGO, DOCUMENTO, IDENTIFICACAO, NULL, NULL, NULL);
SELECT LAST_INSERT_ID() INTO DOCUMENTACAO;

UPDATE GER_ENTIDADE SET
ENT_CODIGO_DOCUMENTO = DOCUMENTACAO,
ENT_IDENTIFICACAO = IDENTIFICACAO
WHERE ENT_CODIGO = CODIGO;

INSERT INTO REST_FUNCIONARIOS(GAR_CODIGO_ENTIDADE, GAR_STATUS, GAR_FILIAL, GAR_COMISSAO)
VALUES(CODIGO, 'A', FILIAL, PROFISSAO);

/*
INSERT INTO RH_FUNCIONARIO(FUN_CODIGO, FUN_DATA_ADMISSAO, FUN_DATA_DEMISSAO, FUN_EXTENSAO,
FUN_INSS, FUN_CODIGO_CARGO, FUN_CODIGO_GRUPO_SALARIAL,  FUN_CODIGO_REGIME, FUN_SALARIO_BASE, 
FUN_ULTIMA_REVISAO_SALARIAL, FUN_CONTA_BANCARIA, FUN_STATUS) 
VALUES(CODIGO, ADMISSAO, DEMISSAO, EXTENSAO, INSS, CARGO, GRUPO, REGIME,
SALARIO, REVISAO, CONTA_BANCARIA, ESTADO); */

ELSE IF (PROFISSIONAL_ID >0) THEN
SET LSTID:=-3;
ELSE IF (DOCUMENTACAO > 0) THEN
SET LSTID:=-2;
