DROP PROCEDURE IF EXISTS `stp_CLI_PROFISSIONAL_ALTERAR`;
delimiter ;;
CREATE PROCEDURE `stp_CLI_PROFISSIONAL_ALTERAR`(IN `CODIGO` integer, IN`NOME_COMPLETO` VARCHAR(80), 
IN `DATA_NASCIMENTO` date,IN `LOCAL_NASCIMENTO` integer,IN `NACIONALIDADE` integer,IN `IDENTIFICACAO`varchar(18),
IN `LOCAL_MORADA` integer,IN `RUA` varchar(50),IN `BAIRRO` varchar(50),IN `TELEFONE` varchar(18),IN `TELF_ALT` varchar(18),
IN `FAX` varchar(18),IN `EMAIL` varchar(40), IN `SEXO` varchar(1),IN `ESTADO_CIVIL` varchar(1), IN `DOCUMENTO` integer,
IN `NATURALIDADE` integer, IN `PAIS` integer, IN `FILIAL` integer, IN `ESPECIALIDADE` integer, IN `NUM_ORDEM` VARCHAR(10),
IN `FOTOGRAFIA` longblob,IN `EXTENSAO` varchar(5), IN `PATH` longtext, IN `PROFISSAO` int,IN `UTILIZADOR` varchar(20),
OUT LSTID INT)
BEGIN
	#Routine body goes here...
DECLARE DOCUMENTACAO INT DEFAULT 0;
DECLARE PROFISSIONAL_ID INT DEFAULT 0;
DECLARE ENTIDADE INT DEFAULT 0;
DECLARE BI VARCHAR(18);

SELECT ENT_IDENTIFICACAO INTO  BI FROM GER_ENTIDADE WHERE ENT_CODIGO = CODIGO;

IF(BI<>IDENTIFICACAO) THEN
 SELECT ENT_CODIGO INTO ENTIDADE FROM GER_ENTIDADE WHERE ENT_IDENTIFICACAO = IDENTIFICACAO;
END IF; 

SELECT IFNULL(PROF_ENTIDADE_ID, 0) INTO PROFISSIONAL_ID FROM CLI_PROFISSIONAIS WHERE PROF_NRO_ORDEM=NUM_ORDEM AND
PROF_CATEGORIA_PROFISSIONAL = PROFISSAO;

IF(ENTIDADE=0) THEN

	IF((PROFISSIONAL_ID = CODIGO OR PROFISSIONAL_ID = 0)) THEN

			UPDATE GER_ENTIDADE SET 
			ENT_NOME_COMPLETO = NOME_COMPLETO,
			ENT_DATA_NASCIMENTO=DATA_NASCIMENTO,
			ENT_LOCAL_NASCIMENTO =LOCAL_NASCIMENTO, 
			ENT_CODIGO_PAIS= PAIS,
			ENT_NACIONALIDADE = NACIONALIDADE,	
			ENT_LOCAL_MORADA = LOCAL_MORADA, 
			ENT_RUA = RUA, ENT_BAIRRO=BAIRRO,
			ENT_TELEFONE=TELEFONE,
			ENT_TELEFONE_ALTERNATIVO=TELF_ALT, 
			ENT_TELEFONE_FAX=FAX, ENT_EMAIL=EMAIL
			WHERE ENT_CODIGO = CODIGO;

			UPDATE GER_ENTIDADE_PESSOA SET
			PES_NOME = NOME_COMPLETO, PES_SOBRENOME = NOME_COMPLETO, PES_SEXO = SEXO, PES_ESTADO_CIVIL = ESTADO_CIVIL,
			PES_FOTOGRAFIA = FOTOGRAFIA, PES_FOTO_EXTENSAO = EXTENSAO, PES_FOTO_PATH = PATH 
			WHERE PES_CODIGO = CODIGO;

		/*
		UPDATE RH_FUNCIONARIO SET FUN_DATA_ADMISSAO = ADMISSAO, FUN_DATA_DEMISSAO=DEMISSAO, FUN_EXTENSAO=EXTENSAO,
		FUN_INSS=INSS,FUN_CODIGO_CARGO=CARGO, FUN_CODIGO_GRUPO_SALARIAL=GRUPO,  FUN_CODIGO_REGIME=REGIME, FUN_SALARIO_BASE=SALARIO, 
		FUN_CONTA_BANCARIA = CONTA_BANCARIA, FUN_STATUS = ESTADO WHERE FUN_CODIGO = CODIGO; 
		*/

		UPDATE CLI_PROFISSIONAIS SET 
		PROF_ENTIDADE_ID = CODIGO,
		PROF_CATEGORIA_PROFISSIONAL = PROFISSAO,
		PROF_NRO_ORDEM = NUM_ORDEM,
		PROF_ESPECIALIDADE_CLINCIA_ID =ESPECIALIDADE,
		PROF_UPDATED_BY = UTILIZADOR,
		PROF_UPDATED_DATE = NOW(),
		PROF_FILIAL_ID = FILIAL
		WHERE PROF_ENTIDADE_ID = CODIGO; 

		SELECT IFNULL(ENT_CODIGO_DOCUMENTO,0) INTO DOCUMENTACAO FROM GER_ENTIDADE
		WHERE ENT_CODIGO = CODIGO;

		IF DOCUMENTACAO = 0 THEN
			
			INSERT INTO GER_ENTIDADE_DOCUMENTACAO (ENT_DOC_CODIGO_ENTIDADE, ENT_DOC_CODIGO_DOCUMENTO, ENT_DOC_NUMERO) 
			VALUES(CODIGO, DOCUMENTO, IDENTIFICACAO);
			SELECT LAST_INSERT_ID() INTO DOCUMENTACAO;
			 ELSE
			UPDATE GER_ENTIDADE_DOCUMENTACAO SET
			ENT_DOC_CODIGO_ENTIDADE=CODIGO, 
			ENT_DOC_CODIGO_DOCUMENTO=DOCUMENTO, 
			ENT_DOC_NUMERO=IDENTIFICACAO 
			WHERE ENT_DOC_CODIGO = DOCUMENTACAO; 
		END IF;

		UPDATE GER_ENTIDADE SET
		ENT_CODIGO_DOCUMENTO = DOCUMENTACAO,
		ENT_IDENTIFICACAO = IDENTIFICACAO
		WHERE ENT_CODIGO = CODIGO;

		SET LSTID:=CODIGO;

	ELSE
	 SET LSTID:=-2;
	END IF;
ELSE
  SET LSTID:=-3;
END IF;

END
;;
delimiter ;

