DROP PROCEDURE IF EXISTS `stp_CLI_PROFISSIONAL_OBTERPORFILTRO`;
delimiter ;;
CREATE PROCEDURE `stp_CLI_PROFISSIONAL_OBTERPORFILTRO`(IN PROFISSIONAL_ID INT, IN `NOME` varchar(120), IN `CATEGORIA` int, IN `ESPECIALIDADE` int)
BEGIN
	#Routine body goes here...
DECLARE vCONSULTA LONGTEXT;
SET vCONSULTA = CONCAT("
SELECT E.ENT_CODIGO, CONCAT(CAT_PROF_TRATAMENTO,' ',E.ENT_NOME_COMPLETO) AS ENT_NOME_COMPLETO, ESP_DESCRICAO, PROF_NRO_ORDEM, PAI_NACIONALIDADE, E.ENT_TELEFONE, 
E.ENT_TELEFONE_ALTERNATIVO, PF.PES_SEXO, CAT_PROF_DESCRICAO FROM GER_ENTIDADE E
INNER JOIN GER_ENTIDADE_PESSOA PF ON PF.PES_CODIGO = E.ENT_CODIGO 
INNER JOIN CLi_PROFISSIONAIS ME ON ME.PROF_ENTIDADE_ID = ENT_CODIGO 
INNER JOIN CLI_ESPECIALIDADE  ON ESP_CODIGO = ME.PROF_ESPECIALIDADE_CLINCIA_ID
INNER JOIN CLI_CATEGORIA_PROFISSIONAL ON CAT_PROF_CODIGO = PROF_CATEGORIA_PROFISSIONAL
INNER JOIN GER_PAIS ON PAI_CODIGO = ENT_CODIGO_PAIS
WHERE (ENT_NOME_COMPLETO LIKE '%",NOME,"%' OR ENT_TELEFONE LIKE '%",NOME,"%' OR PROF_NRO_ORDEM LIKE '%",NOME,"%')");
IF(CATEGORIA > 0) THEN
 SET vCONSULTA = CONCAT(vCONSULTA, " AND PROF_CATEGORIA_PROFISSIONAL = ",CATEGORIA); 
END IF;

IF(ESPECIALIDADE > 0) THEN
SET vCONSULTA = CONCAT(vCONSULTA, " AND PROF_ESPECIALIDADE_CLINCIA_ID = ",ESPECIALIDADE); 
END IF;


#SELECT vCONSULTA;

SET vCONSULTA = CONCAT(vCONSULTA," ORDER BY ENT_NOME_COMPLETO ");
SET @stmt_str = vCONSULTA;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;
 
END
;;
delimiter ;

