delimiter ;;
CREATE PROCEDURE `stp_CLI_PROFISSIONAL_OBTERPORPK`(IN `CODIGO` int)
BEGIN
	#Routine body goes here...
SELECT ENT_CODIGO, ENT_NOME_COMPLETO, ENT_DATA_NASCIMENTO, PES_SEXO, PES_ESTADO_CIVIL, IFNULL(ENT_NACIONALIDADE, -1) AS ENT_NACIONALIDADE, ENT_CODIGO_PAIS, IFNULL(PES_NATURALIDADE, MN.MUN_CODIGO_PROVINCIA) AS PES_NATURALIDADE, ENT_LOCAL_NASCIMENTO, IFNULL(ENT_DOC_CODIGO_DOCUMENTO, -1) AS ENT_DOC_CODIGO_DOCUMENTO, ENT_IDENTIFICACAO, PROF_NRO_ORDEM, PROF_ESPECIALIDADE_CLINCIA_ID, ENT_RUA, ENT_BAIRRO, IFNULL(MM.MUN_CODIGO_PROVINCIA, -1) AS ENT_PROVINCIA_MORADA, IFNULL(ENT_LOCAL_MORADA, -1) AS ENT_LOCAL_MORADA, ENT_TELEFONE, ENT_TELEFONE_ALTERNATIVO, ENT_TELEFONE_FAX, ENT_EMAIL, ENT_FOTOGRAFIA_PATH, PROF_CATEGORIA_PROFISSIONAL, CONCAT(CAT_PROF_TRATAMENTO, " ", ENT_NOME_COMPLETO) AS TRATAMENTO, PROF_CODIGO     FROM GER_ENTIDADE E
INNER JOIN GER_ENTIDADE_PESSOA PF ON PF.PES_CODIGO = E.ENT_CODIGO 
INNER JOIN CLI_PROFISSIONAIS MD ON MD.PROF_ENTIDADE_ID = ENT_CODIGO
LEFT JOIN GER_ENTIDADE_DOCUMENTACAO DC ON DC.ENT_DOC_CODIGO = E.ENT_CODIGO_DOCUMENTO
INNER JOIN CLI_ESPECIALIDADE ON ESP_CODIGO = PROF_ESPECIALIDADE_CLINCIA_ID 
LEFT JOIN GER_MUNICIPIO MM ON MUN_CODIGO = ENT_LOCAL_MORADA 
INNER JOIN GER_MUNICIPIO MN ON MN.MUN_CODIGO = ENT_LOCAL_NASCIMENTO 
INNER JOIN CLI_CATEGORIA_PROFISSIONAL ON CAT_PROF_CODIGO = PROF_CATEGORIA_PROFISSIONAL
WHERE ENT_CODIGO = CODIGO
;
END
;;
