delimiter ;;
CREATE PROCEDURE `stp_COM_ARTIGO_ADICIONAR_STOCK_INICIAL`(IN `QUANTIDADE` decimal,IN `WAREHOUSE_ID` int,IN `PRODUCT_ID` int, IN BARCODE VARCHAR(50), UTILIZADOR varchar(20), MOVIMENT_ID INT, UNIDADE_ID INT, VALIDADE DATE, PRECO_COMPRA DECIMAL(18,2), PRECO_VENDA DECIMAL(18,2), DATA_SI DATETIME)
BEGIN
	#Routine body goes here...
 
IF(PRECO_COMPRA IS NULL) THEN
SET PRECO_COMPRA =0;
END IF;

IF(UNIDADE_ID IS NULL OR UNIDADE_ID =-1) THEN
SELECT ART_UNIDADE_VENDA INTO UNIDADE_ID FROM GER_ARTIGO WHERE ART_CODIGO = PRODUCT_ID;
END IF;

IF(EXISTS(SELECT * FROM COM_STOCK WHERE STOCK_CODIGO_ARMAZEM=WAREHOUSE_ID AND STOCK_CODIGO_ARTIGO=PRODUCT_ID)) THEN

UPDATE COM_STOCK SET
STOCK_QUANTIDADE = QUANTIDADE,
STOCK_ULTIMO_PRECO_CUSTO = PRECO_COMPRA,
STOCK_CUSTO_MEDIO = PRECO_COMPRA,
STOCK_UPDATED_BY = UTILIZADOR,
STOCK_UPDATED_DATE = NOW() 
WHERE STOCK_CODIGO_ARMAZEM=WAREHOUSE_ID AND STOCK_CODIGO_ARTIGO=PRODUCT_ID;
ELSE
INSERT INTO COM_STOCK(STOCK_CODIGO_ARMAZEM, STOCK_CODIGO_ARTIGO, STOCK_QUANTIDADE, STOCK_ULTIMO_PRECO_CUSTO, STOCK_DATA_ULTIMA_ENTRADA,  STOCK_CUSTO_MEDIO, STOCK_DATA_ULTIMA_SAIDA, 
STOCK_DATA_ULTIMA_CONTAGEM, STOCK_CREATED_BY, STOCK_CREATED_DATE, STOCK_UPDATED_BY, STOCK_UPDATED_DATE) 
VALUES(WAREHOUSE_ID, PRODUCT_ID, QUANTIDADE, PRECO_COMPRA, DATA_SI, PRECO_COMPRA, DATA_SI, DATA_SI, UTILIZADOR, NOW(), UTILIZADOR, NOW()); 

END IF;

UPDATE GER_ARTIGO SET
ART_UNIDADE_VENDA = UNIDADE_ID,
ART_PRECO_VENDA = PRECO_VENDA,
ART_PRECO_CUSTO = PRECO_COMPRA,
ART_PCM_CALCULADO = PRECO_COMPRA
WHERE ART_CODIGO = PRODUCT_ID;

IF(BARCODE IS NOT NULL AND BARCODE!="") THEN
 UPDATE GER_ARTIGO SET 
 ART_CODIGO_BARRAS = BARCODE
 WHERE ART_CODIGO = PRODUCT_ID;
END IF;



END
