DROP PROCEDURE IF EXISTS `stp_COM_ARTIGO_EXTRACTO_VENDAS`;
delimiter ;;
CREATE PROCEDURE `stp_COM_ARTIGO_EXTRACTO_VENDAS`(IN `ARTIGO` int, DATA_INI DATE, DATA_TERM DATE, SALESMAN_ID INT)
BEGIN
	#Routine body goes here...
	DECLARE vCONSULTA LONGTEXT;
	SET vCONSULTA = CONCAT("
SELECT FAT_REFERENCIA, FAT_DATA_EMISSAO, A.ART_DESIGNACAO, IFNULL(FAT_ITEM_PRECO_CUSTO, 0) AS FAT_ITEM_PRECO_CUSTO, FAT_ITEM_QUANTIDADE, 
F.FAT_ITEM_PRECO,  FAT_ITEM_VALOR_DESCONTOS,  FAT_ITEM_VALOR_IMPOSTOS, FAT_ITEM_TOTAL, ART_MARGEM_LUCRO,
FAT_NOME_CLIENTE, FAT_COMISSAO, FAT_VALOR_COMISSAO, ENT_NOME_COMPLETO, DOC_FORMATO, D.FAT_STATUS,
IFNULL(FAT_RECTIFIER_DOCUMENT_ID, -1) AS FAT_RECTIFIER_DOCUMENT_ID
FROM 
COM_FATURA_CLIENTE_ITEM F
INNER JOIN GER_ARTIGO A ON A.ART_CODIGO=F.FAT_ITEM_CODIGO_ARTIGO
INNER JOIN COM_FATURA_CLIENTE D ON F.FAT_ITEM_CODIGO_FATURA=D.FAT_CODIGO
INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = FAT_CODIGO_SERIE
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = SER_CODIGO_DOCUMENTO
LEFT JOIN REST_FUNCIONARIOS ON GAR_CODIGO = FAT_VENDEDOR_ID
LEFT JOIN GER_ENTIDADE ON ENT_CODIGO = GAR_CODIGO_ENTIDADE
WHERE  DOC_FORMATO IN ('INVOICE', 'INVOICE_D', 'INVOICE_R', 'INVOICE_P') AND (FAT_ITEM_STATUS IS NULL OR FAT_ITEM_STATUS=1) AND FAT_STATUS_DOCUMENTO >= 7 ");

IF(ARTIGO > 0) THEN

SET vCONSULTA = CONCAT(vCONSULTA," AND FAT_ITEM_CODIGO_ARTIGO =", ARTIGO);

END IF;

IF(DATA_INI IS NOT NULL  AND DATA_TERM IS NOT NULL) THEN
	 SET vCONSULTA =CONCAT(vCONSULTA," AND  (FAT_DATA_EMISSAO BETWEEN  '",DATA_INI, "' AND '",DATA_TERM, "' 
	 OR FAT_DATA_EMISSAO BETWEEN  '",DATA_INI, "' AND '",DATA_TERM, "')"); 
END IF; 

IF(SALESMAN_ID > 0) THEN
SET vCONSULTA = CONCAT(vCONSULTA," AND GAR_CODIGO_ENTIDADE =", SALESMAN_ID);

END IF;

#SELECT vCONSULTA;

SET vCONSULTA = CONCAT(vCONSULTA," ORDER BY FAT_DATA_EMISSAO ");
SET @stmt_str = vCONSULTA;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;
END
;;
delimiter ;

