delimiter ;;
CREATE PROCEDURE `stp_COM_ARTIGO_TABELA_PRECO_OBTERPORFILTRO`(IN `PRODUCT_ID` int, IN `TABELA_ID` int, IN `CATEGORIA_ID` INT)
BEGIN
	#Routine body goes here...

DECLARE vCONSULTA LONGTEXT;
IF( PRODUCT_ID > 0) THEN
   
IF(NOT EXISTS(SELECT * FROM COM_TABELA_PRECO_ARTIGOS WHERE PVP_ARTIGO = PRODUCT_ID))THEN

	INSERT INTO COM_TABELA_PRECO_ARTIGOS (PVP_ARTIGO,
	PVP_TABELA,
	PVP_PRECO,
	PVP_IMPOSTO_ID,
	PVP_UNIDADE_ID,
	PVP_IMPOSTO,
	PVP_VALOR_IMPOSTO,
	PVP_QTD,
	PVP_IMPOSTO_INCLUIDO,
	PVP_CREATED_BY,
	PVP_CREATED_DATE,
	PVP_UPDATED_BY,
	PVP_UPDATED_DATE
	)
	SELECT ART_CODIGO, 1, ART_PRECO_VENDA, ART_CODIGO_IMPOSTO, ART_UNIDADE_VENDA, ART_PERCENT_IMPOSTO, ART_VALOR_IMPOSTO,
	ART_QTD_VENDA, ART_IMPOSTO_INCLUIDO, ART_CREATED_BY, NOW(), ART_CREATED_BY, NOW()
	FROM GER_ARTIGO WHERE ART_CODIGO = PRODUCT_ID;

	END IF;

END IF;

SET vCONSULTA = CONCAT("
  SELECT
	A.ART_CODIGO,
	A.ART_CODIGO_BARRAS,
  A.ART_REFERENCIA,
  UPPER(A.ART_DESIGNACAO) AS ART_DESIGNACAO,
  PVP_TABELA,
  PRE_DESCRICAO,
  PVP_PRECO,
  PVP_IMPOSTO,
  PVP_VALOR_IMPOSTO,
  IFNULL(PRE_DEFAULT, 0) AS PRE_DEFAULT,
  PVP_IMPOSTO_ID,
  PVP_UNIDADE_ID,
  PVP_QTD,
  PVP_IMPOSTO_INCLUIDO
  
FROM GER_ARTIGO A
INNER JOIN COM_TABELA_PRECO_ARTIGOS ON PVP_ARTIGO = ART_CODIGO
INNER JOIN COM_TABELA_PRECO ON PRE_CODIGO = PVP_TABELA 
WHERE 1=1");

IF(PRODUCT_ID > 0) THEN 
    SET vCONSULTA = CONCAT(vCONSULTA," AND ART_CODIGO =", PRODUCT_ID); 
END IF;

IF(TABELA_ID > 0) THEN
    
    SET vCONSULTA = CONCAT(vCONSULTA," AND PVP_TABELA =", TABELA_ID); 
END IF;

IF(CATEGORIA_ID > 0) THEN
    
    SET vCONSULTA = CONCAT(vCONSULTA," AND ART_CATEGORIA =", CATEGORIA_ID); 
END IF;
 
SET @stmt_str = vCONSULTA;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;

END
;;
delimiter ;

