-- Procedure structure for stp_COM_CONTA_CORRENTE_CLIENTE_EXTRATO
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_COM_CONTA_CORRENTE_CLIENTE_EXTRATO`;
delimiter ;;
CREATE PROCEDURE `stp_COM_CONTA_CORRENTE_CLIENTE_EXTRATO`(IN `ENTITY_ID` int)
BEGIN
	#Routine body goes here...

SELECT
	FAT_CODIGO,
	FAT_DATA_EMISSAO,
	SER_DESCRICAO,
	FAT_REFERENCIA,
	DOC_DESCRICAO,
	FAT_NOME_CLIENTE,
	(FAT_VALOR_TOTAL - IFNULL(FAT_VALOR_RETENCAO,0)) AS FAT_VALOR_TOTAL,
	(FAT_VALOR_PAGO - FAT_TROCO) AS FAT_VALOR_PAGO,
  0 AS FAT_EXCESSO, 
	DOC_FORMATO,
	FAT_CREATED_DATE,
	TER_CODIGO AS FAT_CODIGO_CLIENTE,
  DOC_CODIGO,
  MOE_SIGLA,
  FAT_CAMBIO,
  FAT_PARCELA,
	FAT_NUMERO_DOC_ORIGEM,
	ENT_EMAIL 
FROM
	COM_FATURA_CLIENTE C
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = FAT_CODIGO_DOCUMENTO
INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = FAT_CODIGO_SERIE
LEFT JOIN GER_ENTIDADE ON ENT_CODIGO = FAT_CODIGO_CLIENTE
INNER JOIN FIN_MOEDA ON MOE_CODIGO = FAT_CODIGO_MOEDA
INNER JOIN GER_TERCEIROS ON TER_CODIGO_ENTIDADE = ENT_CODIGO
WHERE (FAT_STATUS = 1 OR FAT_RECTIFIER_DOCUMENT_ID > 0)
AND FAT_CODIGO_CLIENTE = ENTITY_ID
AND FAT_NUMERACAO > 0
UNION
	SELECT
		REC_CODIGO,
		REC_DATA_EMISSAO,
		SER_DESCRICAO,
		REC_REFERENCIA,
		DOC_DESCRICAO,
		REC_NOME_CLIENTE,
		REC_VALOR_TOTAL,
		REC_VALOR_PAGO AS FAT_VALOR_PAGO,
		IFNULL(REC_VALOR_EXCESSO, 0) AS FAT_EXCESSO,
		DOC_FORMATO,
		REC_CREATED_DATE AS FAT_CREATED_DATE,
		TER_CODIGO AS FAT_CODIGO_CLIENTE,
    DOC_CODIGO,
    MOE_SIGLA,
    REC_CAMBIO,
		1 AS FAT_PARCELA,
	  IFNULL(REC_DOCUMENT_FROM_ID, 0) AS FAT_NUMERO_DOC_ORIGEM,
		ENT_EMAIL
	FROM
		COM_RECIBO_CLIENTE C
	LEFT JOIN GER_TERCEIROS ON TER_CODIGO = REC_CODIGO_ENTIDADE
	INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = REC_CODIGO_DOCUMENTO
	INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = REC_CODIGO_SERIE
  INNER JOIN FIN_MOEDA ON MOE_CODIGO = REC_CODIGO_MOEDA
	LEFT JOIN GER_ENTIDADE ON ENT_CODIGO = TER_CODIGO_ENTIDADE
	WHERE
		TER_CODIGO_ENTIDADE = ENTITY_ID
	AND REC_NUMERACAO > 0
	AND REC_STATUS = 1

ORDER BY FAT_CREATED_DATE;

END
;;
delimiter ;

