-- ----------------------------
-- Procedure structure for stp_COM_DOCUMENTOS_RELACIONADOS_OBTERPORFILTRO
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_COM_DOCUMENTOS_RELACIONADOS_OBTERPORFILTRO`;
delimiter ;;
CREATE PROCEDURE `stp_COM_DOCUMENTOS_RELACIONADOS_OBTERPORFILTRO`(IN `DOC_ID` int,IN `DOC_TYPE_ID` int)
BEGIN
	#Routine body goes here...
 
SELECT FG.FAT_NUMERO_DOC_ORIGEM, FG.FAT_CODIGO, FG.FAT_DOCUMENTO_ORIGEM, FG.FAT_CODIGO_DOCUMENTO, O.DOC_DESCRICAO AS DOC_ORIGEM, R.DOC_DESCRICAO AS DOC_GENERATED,
FO.FAT_REFERENCIA AS REF_ORIGEM, FG.FAT_REFERENCIA AS REF_GENERATED
FROM COM_FATURA_CLIENTE FG
LEFT JOIN COM_DOCUMENTO O ON O.DOC_CODIGO = FG.FAT_DOCUMENTO_ORIGEM 
LEFT JOIN COM_DOCUMENTO R ON R.DOC_CODIGO = FG.FAT_CODIGO_DOCUMENTO 
INNER JOIN COM_FATURA_CLIENTE FO ON FO.FAT_CODIGO = FG.FAT_NUMERO_DOC_ORIGEM
WHERE FG.FAT_CODIGO = DOC_ID AND FG.FAT_CODIGO_DOCUMENTO = DOC_TYPE_ID
UNION  
SELECT FG.FAT_NUMERO_DOC_ORIGEM, FG.FAT_CODIGO, FG.FAT_DOCUMENTO_ORIGEM, FG.FAT_CODIGO_DOCUMENTO, O.DOC_DESCRICAO AS DOC_ORIGEM, R.DOC_DESCRICAO AS DOC_GENERATED,
FO.FAT_REFERENCIA AS REF_ORIGEM, FG.FAT_REFERENCIA AS REF_GENERATED
FROM COM_FATURA_CLIENTE FG
LEFT JOIN COM_DOCUMENTO O ON O.DOC_CODIGO = FG.FAT_DOCUMENTO_ORIGEM 
LEFT JOIN COM_DOCUMENTO R ON R.DOC_CODIGO = FG.FAT_CODIGO_DOCUMENTO 
INNER JOIN COM_FATURA_CLIENTE FO ON FO.FAT_CODIGO = FG.FAT_NUMERO_DOC_ORIGEM
WHERE FG.FAT_NUMERO_DOC_ORIGEM = DOC_ID AND FG.FAT_DOCUMENTO_ORIGEM = DOC_TYPE_ID
UNION
SELECT REC_DOC_DOCUMENTO_ID, REC_CODIGO, REC_DOC_DOCUMENT_TYPE, REC_CODIGO_DOCUMENTO, F.DOC_DESCRICAO AS DOC_ORIGEM, R.DOC_DESCRICAO AS RECEIPT_GENERATED,
REC_DOC_REFERENCIA AS REF_ORIGEM, REC_REFERENCIA AS REF_GENERATED
FROM COM_RECIBO_CLIENTE_DOCUMENTOS
LEFT JOIN com_recibo_cliente ON REC_CODIGO = REC_DOC_RECIBO_ID
LEFT JOIN COM_DOCUMENTO R ON R.DOC_CODIGO = REC_CODIGO_DOCUMENTO
LEFT JOIN COM_DOCUMENTO F ON F.DOC_CODIGO = REC_DOC_DOCUMENT_TYPE
WHERE REC_DOC_DOCUMENTO_ID = DOC_ID AND REC_DOC_DOCUMENT_TYPE = DOC_TYPE_ID
UNION 
SELECT MOV_DOCUMENT_ID, MOV_CODIGO,  MOV_DOCUMENT_FROM, MOV_CODIGO_DOCUMENTO,  FACT.DOC_DESCRICAO AS DOC_ORIGEM, STOCK.DOC_DESCRICAO AS STOCK_MOVIMENT_GENERATED,  
FAT_REFERENCIA AS REF_ORIGEM, MOV_REFERENCIA AS REF_GENERATED 
FROM STOCK_MOVIMENTO_DIRECTOS 
INNER JOIN COM_FATURA_CLIENTE ON FAT_CODIGO = MOV_DOCUMENT_ID
INNER JOIN COM_DOCUMENTO STOCK ON STOCK.DOC_CODIGO = MOV_CODIGO_DOCUMENTO
INNER JOIN COM_DOCUMENTO FACT ON FACT.DOC_CODIGO = MOV_DOCUMENT_FROM
WHERE MOV_DOCUMENT_FROM = DOC_TYPE_ID AND MOV_DOCUMENT_ID =DOC_ID

#DOCUMENTOS DE FORNECEDORES
UNION
SELECT FG.FAT_NUMERO_DOC_ORIGEM, FG.FAT_CODIGO, FG.FAT_DOCUMENTO_ORIGEM, FG.FAT_CODIGO_DOCUMENTO, O.DOC_DESCRICAO AS DOC_ORIGEM, R.DOC_DESCRICAO AS DOC_GENERATED,
FO.FAT_REFERENCIA AS REF_ORIGEM, FG.FAT_REFERENCIA AS REF_GENERATED
FROM COM_FATURA_FORNECEDOR FG
LEFT JOIN COM_DOCUMENTO O ON O.DOC_CODIGO = FG.FAT_DOCUMENTO_ORIGEM 
LEFT JOIN COM_DOCUMENTO R ON R.DOC_CODIGO = FG.FAT_CODIGO_DOCUMENTO 
INNER JOIN COM_FATURA_FORNECEDOR FO ON FO.FAT_CODIGO = FG.FAT_NUMERO_DOC_ORIGEM
WHERE FG.FAT_CODIGO = DOC_ID /*AND FG.FAT_CODIGO_DOCUMENTO = DOC_TYPE_ID*/
UNION  
SELECT FG.FAT_NUMERO_DOC_ORIGEM, FG.FAT_CODIGO, FG.FAT_DOCUMENTO_ORIGEM, FG.FAT_CODIGO_DOCUMENTO, O.DOC_DESCRICAO AS DOC_ORIGEM, R.DOC_DESCRICAO AS DOC_GENERATED,
FO.FAT_REFERENCIA AS REF_ORIGEM, FG.FAT_REFERENCIA AS REF_GENERATED
FROM COM_FATURA_FORNECEDOR FG
LEFT JOIN COM_DOCUMENTO O ON O.DOC_CODIGO = FG.FAT_DOCUMENTO_ORIGEM 
LEFT JOIN COM_DOCUMENTO R ON R.DOC_CODIGO = FG.FAT_CODIGO_DOCUMENTO 
INNER JOIN COM_FATURA_FORNECEDOR FO ON FO.FAT_CODIGO = FG.FAT_NUMERO_DOC_ORIGEM
WHERE FG.FAT_NUMERO_DOC_ORIGEM = DOC_ID /*AND FG.FAT_DOCUMENTO_ORIGEM = DOC_TYPE_ID*/
UNION
SELECT PAG_DOC_DOCUMENTO_ID, REC_CODIGO, PAG_DOC_DOCUMENT_TYPE, REC_CODIGO_DOCUMENTO, F.DOC_DESCRICAO AS DOC_ORIGEM, R.DOC_DESCRICAO AS RECEIPT_GENERATED,
PAG_DOC_REFERENCIA AS REF_ORIGEM, REC_REFERENCIA AS REF_GENERATED
FROM COM_PAGAMENTO_FORNECEDOR_DOCUMENTOS
LEFT JOIN COM_PAGAMENTO_FORNECEDOR ON REC_CODIGO = PAG_DOC_RECIBO_ID
LEFT JOIN COM_DOCUMENTO R ON R.DOC_CODIGO = REC_CODIGO_DOCUMENTO
LEFT JOIN COM_DOCUMENTO F ON F.DOC_CODIGO = PAG_DOC_DOCUMENT_TYPE
WHERE PAG_DOC_DOCUMENTO_ID = DOC_ID /*AND PAG_DOC_DOCUMENT_TYPE = DOC_TYPE_ID*/
UNION 
SELECT MOV_DOCUMENT_ID, MOV_CODIGO,  MOV_DOCUMENT_FROM, MOV_CODIGO_DOCUMENTO,  FACT.DOC_DESCRICAO AS DOC_ORIGEM, STOCK.DOC_DESCRICAO AS STOCK_MOVIMENT_GENERATED,  
FAT_REFERENCIA AS REF_ORIGEM, MOV_REFERENCIA AS REF_GENERATED 
FROM STOCK_MOVIMENTO_DIRECTOS 
INNER JOIN COM_FATURA_FORNECEDOR ON FAT_CODIGO = MOV_DOCUMENT_ID
INNER JOIN COM_DOCUMENTO STOCK ON STOCK.DOC_CODIGO = MOV_CODIGO_DOCUMENTO
INNER JOIN COM_DOCUMENTO FACT ON FACT.DOC_CODIGO = MOV_DOCUMENT_FROM
WHERE MOV_DOCUMENT_FROM = DOC_TYPE_ID AND MOV_DOCUMENT_ID =DOC_ID;
END
;;
