delimiter ;;
CREATE PROCEDURE `stp_COM_DOCUMENTO_OBTERPERMISSOES`(IN `DOCUMENTO` int,IN `UTILIZADOR` varchar(20))
BEGIN
	#Routine body goes here...
DECLARE PERFIL INT DEFAULT 0;

SELECT UTI_CODIGO_PERFIL INTO PERFIL FROM SIS_UTILIZADOR WHERE UTI_UTILIZADOR = UTILIZADOR;

IF(DOCUMENTO > 0) THEN
SELECT DISTINCT A.DOC_ACE_CODIGO_DOCUMENTO, DOC_DESCRICAO, A.DOC_ACE_DELETE, A.DOC_ACE_INSERT, A.DOC_ACE_SELECT, A.DOC_ACE_UPDATE,
DOC_FORMATO
FROM COM_DOCUMENTO_UTILIZADOR_ACESSO A
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = A.DOC_ACE_CODIGO_DOCUMENTO
LEFT JOIN SIS_UTILIZADOR ON UTI_UTILIZADOR = A.DOC_ACE_UTILIZADOR
LEFT JOIN SIS_PERFIL ON PER_CODIGO = A.DOC_ACE_CODIGO_PERFIL
WHERE A.DOC_ACE_CODIGO_DOCUMENTO = DOCUMENTO AND (A.DOC_ACE_CODIGO_PERFIL = PERFIL OR A.DOC_ACE_UTILIZADOR = UTILIZADOR) AND A.DOC_ACE_DELETE=1 AND A.DOC_ACE_INSERT=1 AND A.DOC_ACE_SELECT=1 AND A.DOC_ACE_UPDATE=1;
ELSE

SELECT DISTINCT A.DOC_ACE_CODIGO_DOCUMENTO, DOC_DESCRICAO, A.DOC_ACE_DELETE, A.DOC_ACE_INSERT, A.DOC_ACE_SELECT, A.DOC_ACE_UPDATE,
DOC_FORMATO
FROM COM_DOCUMENTO_UTILIZADOR_ACESSO A
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = A.DOC_ACE_CODIGO_DOCUMENTO
LEFT JOIN SIS_UTILIZADOR ON UTI_UTILIZADOR = A.DOC_ACE_UTILIZADOR
LEFT JOIN SIS_PERFIL ON PER_CODIGO = A.DOC_ACE_CODIGO_PERFIL
WHERE (A.DOC_ACE_CODIGO_PERFIL = PERFIL OR A.DOC_ACE_UTILIZADOR = UTILIZADOR);
END IF;
END
;;
