-- ----------------------------
-- Procedure structure for stp_COM_ENCOMENDA_OBTER_ITENS
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_COM_ENCOMENDA_OBTER_ITENS`;
delimiter ;;
CREATE PROCEDURE `stp_COM_ENCOMENDA_OBTER_ITENS`(IN `CUSTOMER_ID` int,IN `TELEFONE` varchar(20),IN `DELIVERYMAN_ID` int,IN `EMISSAO_INI` date,IN `EMISSAO_TERM` date,IN `ESTADO` INT,IN `ORDER_ID` int, EMPLOYEE_ID int, ORDER_NUMBER INT)
BEGIN
	#Routine body goes here...
DECLARE v_CON_ LONGTEXT;

SET v_CON_ = CONCAT("
  SELECT I.*, IFNULL(FAT_ITEM_DESGINATION, ART_DESIGNACAO) AS ART_DESIGNACAO, ART_REFERENCIA, ART_CODIGO_BARRAS, UNI_SIGLA, 	 ART_MOVIMENTA_STOCK, FAT_CODIGO_ARMAZEM,
	FAT_NOME_CLIENTE, FAT_RECEPCAO, FAT_LOCAL_DESCARGA, DOC_DESCRICAO, FAT_REFERENCIA, FAT_DATA_EMISSAO, FAT_DATA_DESCARGA 
  AS FAT_DATA_ENTREGA, DOC_FORMATO, FAT_ORIGINAL_CURRENCY_ID, GAR_CODIGO_ENTIDADE, UNI_FACTOR_CONVERSAO, 
	IFNULL(UNI_QUANTIDADE, 1) AS UNI_QUANTIDADE, FU.ENT_NOME_COMPLETO AS NOME_TECNICO_EXECUTOR, 
	FAT_VALOR_TOTAL, FAT_VALOR_PAGO, FAT_CODIGO_CLIENTE
	FROM COM_FATURA_CLIENTE_ITEM I 
	INNER JOIN GER_ARTIGO ON ART_CODIGO = FAT_ITEM_CODIGO_ARTIGO
	INNER JOIN GER_UNIDADE_MEDICAO ON UNI_CODIGO = ART_UNIDADE_VENDA
	INNER JOIN COM_FATURA_CLIENTE ON FAT_CODIGO = I.FAT_ITEM_CODIGO_FATURA
	INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = FAT_CODIGO_DOCUMENTO
	LEFT JOIN REST_FUNCIONARIOS ON GAR_CODIGO = FAT_VENDEDOR_ID 
	LEFT JOIN GER_ENTIDADE FU ON FU.ENT_CODIGO = GAR_CODIGO_ENTIDADE
	LEFT JOIN COM_GUIA_ENTREGA_ITEM ON GUIA_ITEM_CODIGO_ARTIGO = FAT_ITEM_CODIGO
	LEFT JOIN COM_GUIA_ENTREGA ON GUIA_CODIGO = GUIA_ITEM_GUIA_ID
	INNER JOIN GER_ENTIDADE C ON C.ENT_CODIGO = FAT_CODIGO_CLIENTE
	WHERE 1=1 AND DOC_FORMATO='INVOICE_E'");
	
	
	IF(CUSTOMER_ID > 0) THEN 
	 SET v_CON_ = CONCAT(v_CON_," AND FAT_CODIGO_CLIENTE =", CUSTOMER_ID); 
	END IF;
	
	IF(ORDER_ID > 0) THEN 
	 SET v_CON_ = CONCAT(v_CON_," AND FAT_CODIGO =", ORDER_ID);   
	END IF;
	IF(ORDER_NUMBER > 0) THEN 
	 SET v_CON_ = CONCAT(v_CON_," AND FAT_NUMERACAO =", ORDER_NUMBER); 
	END IF;
	IF(DELIVERYMAN_ID > 0) THEN 
	 SET v_CON_ = CONCAT(v_CON_," AND GUIA_CODIGO_FUNCIONARIO =", DELIVERYMAN_ID); 
	END IF;
	IF(TELEFONE != "") THEN 
	 SET v_CON_ = CONCAT(v_CON_," AND ENT_TELEFONE =", TELEFONE); 
	END IF;
	IF(ESTADO =0) THEN
	  SET v_CON_ = CONCAT(v_CON_," AND FAT_ITEM_DATA_ENTREGA IS NOT NULL AND FAT_ITEM_DATA_TERMINO IS NOT NULL "); 
	ELSE IF(ESTADO =1) THEN
	  SET v_CON_ = CONCAT(v_CON_," AND FAT_ITEM_DATA_ENTREGA IS NULL  AND FAT_ITEM_DATA_TERMINO IS NOT NULL "); 
	ELSE IF(ESTADO = -1) THEN
	SET v_CON_ = CONCAT(v_CON_," AND FAT_ITEM_DATA_TERMINO IS NOT NULL ");  
  ELSE
	SET v_CON_ = CONCAT(v_CON_," AND FAT_ITEM_DATA_TERMINO IS NULL ");  
	END IF;
	END IF;
	END IF;
	
	IF((EMISSAO_INI !='' AND EMISSAO_INI IS NOT NULL) AND (EMISSAO_TERM !='' AND EMISSAO_TERM IS NOT NULL)) THEN
  SET v_CON_ = CONCAT(v_CON_," AND FAT_DATA_EMISSAO BETWEEN '",EMISSAO_INI, "' AND '", EMISSAO_TERM, "'");
ELSE IF(EMISSAO_INI IS NOT NULL AND EMISSAO_TERM IS NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND FAT_DATA_EMISSAO >= '",EMISSAO_INI, "'");
ELSE IF(EMISSAO_INI IS NULL AND EMISSAO_TERM IS NOT NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND FAT_DATA_EMISSAO <= '", EMISSAO_TERM, "'");
END IF;
END IF;
END IF;
#SELECT v_CON_;
SET @stmt_str = v_CON_;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;
 
END
;;
delimiter ;

