CREATE PROCEDURE `stp_COM_FATURA_CLIENTE_EXCLUIR`(IN `CODIGO` INT, IN UTILIZADOR VARCHAR(20), IN MOTIVO VARCHAR(120))
BEGIN
	#Routine body goes here...
DECLARE MOVIMENTO INT DEFAULT 0;
DECLARE TOTAL_DOCS INT DEFAULT 0;
DECLARE RECIBO_ID INT DEFAULT 0;
DECLARE RECIBO_TYPE INT DEFAULT 0; 


SELECT COUNT(REC_DOC_DOCUMENTO_ID) INTO TOTAL_DOCS FROM COM_RECIBO_CLIENTE_DOCUMENTOS WHERE REC_DOC_DOCUMENTO_ID = CODIGO;
 
IF(TOTAL_DOCS IS NULL OR TOTAL_DOCS <=0) THEN
	

	SELECT COUNT(MOV_CODIGO_FATURA) INTO TOTAL_DOCS FROM FIN_MOVIMENTO_FATURA WHERE MOV_CODIGO_FATURA = CODIGO AND MOV_FAT_STATUS !=0; 
	WHILE(TOTAL_DOCS > 0) DO
	 SELECT MOV_CODIGO_MOVIMENTO INTO MOVIMENTO FROM FIN_MOVIMENTO_FATURA WHERE MOV_CODIGO_FATURA = CODIGO LIMIT 1; 
	 UPDATE  COM_POS_MOVIMENTO SET POS_MOV_STATUS = 0 WHERE POS_MOV_CODIGO_MOVIMENTO = MOVIMENTO;
	 UPDATE FIN_MOVIMENTO_CAIXA SET MOV_STATUS =0 WHERE MOV_CODIGO = MOVIMENTO;
   UPDATE FIN_MOVIMENTO_FATURA SET MOV_FAT_STATUS =0 WHERE MOV_CODIGO_FATURA = CODIGO; 
	 SELECT COUNT(MOV_CODIGO_FATURA) INTO TOTAL_DOCS FROM FIN_MOVIMENTO_FATURA WHERE MOV_CODIGO_FATURA = CODIGO AND MOV_FAT_STATUS !=0;
	END WHILE; 

		UPDATE STOCK_MOVIMENTO_DIRECTOS SET
		MOV_DELETED = 1,
		MOV_DELETED_BY = UTILIZADOR,
		MOV_DELETED_DATE = NOW()
		WHERE MOV_DOCUMENT_ID = CODIGO; 

