WHERE FAT_ITEM_CODIGO_ARTIGO = ARTIGO AND FAT_ITEM_CODIGO_FATURA = FATURA;

UPDATE COM_FATURA_CLIENTE_ITEM SET 
FAT_ITEM_STATUS=0 
WHERE FAT_ITEM_CODIGO_ARTIGO = ARTIGO 
AND FAT_ITEM_CODIGO_FATURA = FATURA;

UPDATE COM_STOCK SET
STOCK_QUANTIDADE = STOCK_QUANTIDADE + PRODUCT_QTY
WHERE STOCK_CODIGO_ARMAZEM = WAREHOUSE_ID AND 
STOCK_CODIGO_ARTIGO = ARTIGO;
END
