-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_COM_FATURA_CLIENTE_ITEM_OBTERPORFILTRO`;
delimiter ;;
CREATE PROCEDURE `stp_COM_FATURA_CLIENTE_ITEM_OBTERPORFILTRO`(IN `FATURA` int, INICIO DATE, FINAL DATE)
BEGIN
	#DELETE FROM COM_FATURA_CLIENTE_ITEM WHERE FAT_ITEM_STATUS =0;
	#Routine body goes here...
IF(FATURA > 0) THEN 

	SELECT I.*, IFNULL(FAT_ITEM_DESGINATION, ART_DESIGNACAO) AS ART_DESIGNACAO, ART_REFERENCIA, ART_CODIGO_BARRAS, UNI_SIGLA, 	 ART_MOVIMENTA_STOCK, FAT_CODIGO_ARMAZEM,
	FAT_NOME_CLIENTE, FAT_RECEPCAO, FAT_LOCAL_DESCARGA, DOC_DESCRICAO, FAT_REFERENCIA, FAT_DATA_EMISSAO, FAT_DATA_DESCARGA 
  AS FAT_DATA_ENTREGA, DOC_FORMATO, FAT_ORIGINAL_CURRENCY_ID, GAR_CODIGO_ENTIDADE, UNI_FACTOR_CONVERSAO, IFNULL(UNI_QUANTIDADE, 1) AS UNI_QUANTIDADE,   FU.ENT_NOME_COMPLETO AS NOME_TECNICO_EXECUTOR, ART_IMAGEM, ART_TIPO 
	FROM COM_FATURA_CLIENTE_ITEM I
	INNER JOIN GER_ARTIGO ON ART_CODIGO = FAT_ITEM_CODIGO_ARTIGO
	INNER JOIN GER_UNIDADE_MEDICAO ON UNI_CODIGO = ART_UNIDADE_VENDA
	INNER JOIN COM_FATURA_CLIENTE ON FAT_CODIGO = I.FAT_ITEM_CODIGO_FATURA
	INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = FAT_CODIGO_DOCUMENTO
	LEFT JOIN REST_FUNCIONARIOS ON GAR_CODIGO = FAT_VENDEDOR_ID 
	LEFT JOIN GER_ENTIDADE FU ON FU.ENT_CODIGO = GAR_CODIGO_ENTIDADE
	WHERE FAT_ITEM_CODIGO_FATURA = FATURA /*AND (FAT_ITEM_STATUS IS NULL OR FAT_ITEM_STATUS > 0)*/ ORDER BY FAT_ORDEM;

ELSE

	SELECT I.*, IFNULL(FAT_ITEM_DESGINATION, ART_DESIGNACAO) AS ART_DESIGNACAO, ART_REFERENCIA, ART_CODIGO_BARRAS, UNI_SIGLA, ART_MOVIMENTA_STOCK, FAT_CODIGO_ARMAZEM,
	FAT_NOME_CLIENTE, FAT_RECEPCAO, FAT_LOCAL_DESCARGA, DOC_DESCRICAO, FAT_REFERENCIA, FAT_DATA_EMISSAO, FAT_DATA_DESCARGA 
  AS FAT_DATA_ENTREGA, DOC_FORMATO, FAT_ORIGINAL_CURRENCY_ID, GAR_CODIGO_ENTIDADE, UNI_FACTOR_CONVERSAO, IFNULL(UNI_QUANTIDADE, 1) AS UNI_QUANTIDADE, FU.ENT_NOME_COMPLETO AS NOME_TECNICO_EXECUTOR, ART_IMAGEM, ART_TIPO 
	FROM COM_FATURA_CLIENTE_ITEM I
	INNER JOIN GER_ARTIGO ON ART_CODIGO = FAT_ITEM_CODIGO_ARTIGO
	INNER JOIN GER_UNIDADE_MEDICAO ON UNI_CODIGO = ART_UNIDADE_VENDA
	INNER JOIN COM_FATURA_CLIENTE ON FAT_CODIGO = I.FAT_ITEM_CODIGO_FATURA
	INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = FAT_CODIGO_DOCUMENTO
	LEFT JOIN REST_FUNCIONARIOS ON GAR_CODIGO = FAT_VENDEDOR_ID 
	LEFT JOIN GER_ENTIDADE FU ON FU.ENT_CODIGO = GAR_CODIGO_ENTIDADE
	WHERE FAT_DATA_ENTREGA BETWEEN INICIO AND FINAL /*AND (FAT_ITEM_STATUS IS NULL OR FAT_ITEM_STATUS > 0)*/ ORDER BY FAT_ORDEM; 
END IF;
END
