delimiter ;;
CREATE PROCEDURE `stp_COM_FATURA_CLIENTE_IVA_GLOBAL`(IN `DATA_INI` date,IN `DATA_TERM` date,IN `COMPANY_ID` int,IN `FISCAL_YEAR` int)
BEGIN
	#Routine body goes here...
select IMP_CODIGO, IMP_DESCRICAO, IMP_VALOR, (SUM(FAT_ITEM_PRECO * FAT_ITEM_QUANTIDADE)-SUM(FAT_ITEM_VALOR_DESCONTOS)) AS IMP_INCIDENCIA,
(SUM(FAT_ITEM_PRECO * FAT_ITEM_QUANTIDADE)-SUM(FAT_ITEM_VALOR_DESCONTOS))*(IMP_VALOR/100) AS IVA
from GER_IMPOSTOS
INNER JOIN COM_FATURA_CLIENTE_ITEM ON FAT_ITEM_IMPOSTO_ID = IMP_CODIGO
