-- ----------------------------
-- Procedure structure for stp_COM_FATURA_CLIENTE_OBTERPENDENTES
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_COM_FATURA_CLIENTE_OBTERPENDENTES`;
delimiter ;;
CREATE PROCEDURE `stp_COM_FATURA_CLIENTE_OBTERPENDENTES`(IN FILIAL INT, IN EMISSAO_INI DATE, IN EMISSAO_TERM DATE, IN VALIDADE_INI DATE, IN VALIDADE_TERM DATE, REFERENCIA VARCHAR(12), CLIENTE VARCHAR(120), DOCUMENTO INT, SITUACAO INT, in ENTIDADE INT, in DOCUMENT_STATUS int, IN PAYMENT_STATUS INT, IN SERIE INT, WAREHOUSE_ID INT, UTILIZADOR VARCHAR(20))
BEGIN
	#Routine body goes here...
DECLARE v_CON_ LONGTEXT;
DECLARE PERFIL_ID INT DEFAULT 0;
DECLARE UTILIZADOR_ID INT DEFAULT 0;


SELECT UTI_CODIGO_PERFIL, UTI_CODIGO INTO PERFIL_ID, UTILIZADOR_ID FROM SIS_UTILIZADOR WHERE UTI_UTILIZADOR = UTILIZADOR;
 
SET v_CON_ = CONCAT("

  SELECT 
	FAT_CODIGO, 
	FAT_REFERENCIA, 
	FAT_DATA_EMISSAO, 
	DOC_DESCRICAO, 
	FAT_NOME_CLIENTE, 
	FAT_TOTAL_DESCONTOS, 
	FAT_TOTAL_IMPOSTOS, 
	(FAT_VALOR_TOTAL - IFNULL(FAT_CUSTOMER_CREDIT, 0)) AS FAT_VALOR_TOTAL,
  FAT_VALOR_PAGO,
  FAT_TROCO,
  FAT_DATA_VALIDADE,
  (FAT_VALOR_TOTAL - FAT_VALOR_PAGO) AS  FAT_VALOR_SALDO,
  FAT_STATUS_DOCUMENTO,
  FAT_STATUS_PAGAMENTO,
  SER_DESCRICAO,
  FAT_VALOR_BRUTO,
  FAT_CREATED_BY,
  DOC_FORMATO,
  MOE_SIGLA,
  FAT_CAMBIO,
  FAT_CODIGO_CLIENTE,
  FAT_PARCELA, 
	FAT_VALOR_RETENCAO,
	FAT_NUMERO_DOC_ORIGEM,
  FAT_CUSTOMER_CREDIT,	#Valor Rectificado por Nota de Crédito ou Recibo de Regularizacao de Cliente
	ENT_EMAIL
FROM 
	COM_FATURA_CLIENTE C
#INNER JOIN GER_TERCEIROS ON TER_CODIGO_ENTIDADE = FAT_CODIGO_CLIENTE
INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = FAT_CODIGO_SERIE
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = SER_CODIGO_DOCUMENTO 
INNER JOIN FIN_MOEDA ON MOE_CODIGO = FAT_CODIGO_MOEDA 
INNER JOIN GER_ENTIDADE ON ENT_CODIGO = FAT_CODIGO_CLIENTE
WHERE DOC_FORMATO IN ('INVOICE', 'INVOICE_R', 'INVOICE_D', 'INVOICE_A') AND  FAT_STATUS =1 
AND (((FAT_VALOR_TOTAL - IFNULL(FAT_VALOR_RETENCAO, 0)) - FAT_VALOR_PAGO) > 0 OR (FAT_VALOR_TOTAL - FAT_VALOR_PAGO) < 0) AND C.FAT_CODIGO_FILIAL=",FILIAL, " AND FAT_REFERENCIA LIKE '%",REFERENCIA,"%' AND 
FAT_NOME_CLIENTE LIKE '%",CLIENTE,"%' AND  FAT_STATUS_DOCUMENTO =8 AND FAT_NUMERACAO > 0 ");

IF(EMISSAO_INI <>'' AND EMISSAO_TERM <>'') THEN
  SET v_CON_ = CONCAT(v_CON_," AND FAT_DATA_EMISSAO BETWEEN '",EMISSAO_INI, "' AND '", EMISSAO_TERM, "'");
ELSE IF(EMISSAO_INI IS NOT NULL AND EMISSAO_TERM IS NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND FAT_DATA_EMISSAO >= ",EMISSAO_INI);
ELSE IF(EMISSAO_INI IS NULL AND EMISSAO_TERM IS NOT NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND FAT_DATA_EMISSAO <= ", EMISSAO_TERM);
END IF;
END IF;
END IF;

IF( VALIDADE_INI IS NOT NULL AND  VALIDADE_TERM IS NOT NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND FAT_DATA_VALIDADE BETWEEN '", VALIDADE_INI, "' AND '",  VALIDADE_TERM, "'");
ELSE IF( VALIDADE_INI IS NOT NULL AND  VALIDADE_TERM IS NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND FAT_DATA_VALIDADE >= '", VALIDADE_INI, "'");
ELSE IF( VALIDADE_INI IS NULL AND  VALIDADE_TERM IS NOT NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND FAT_DATA_VALIDADE <= '",  VALIDADE_TERM, "'");
END IF;
END IF;
END IF;
IF(DOCUMENTO > 0) THEN
SET v_CON_=CONCAT(v_CON_," AND FAT_CODIGO_DOCUMENTO = ", DOCUMENTO);
END IF;

IF(SITUACAO > 0) THEN
 SET v_CON_=CONCAT(v_CON_," AND FAT_STATUS > 0 ");
END IF; 

IF(DOCUMENT_STATUS > 0) THEN
 SET v_CON_=CONCAT(v_CON_," AND FAT_STATUS_DOCUMENTO = ", DOCUMENT_STATUS);
END IF; 
/*
IF(PAYMENT_STATUS > 0) THEN
 SET v_CON_=CONCAT(v_CON_," AND FAT_STATUS_PAGAMENTO = ", PAYMENT_STATUS);
END IF; 
*/
IF(ENTIDADE > 0) THEN
SET v_CON_=CONCAT(v_CON_," AND FAT_CODIGO_CLIENTE = ", ENTIDADE); 
END IF;


IF(SERIE > 0) THEN
SET v_CON_=CONCAT(v_CON_," AND FAT_CODIGO_SERIE = ", SERIE);
END IF;

IF(WAREHOUSE_ID > 0) THEN
 SET v_CON_=CONCAT(v_CON_, " AND FAT_CODIGO_ARMAZEM = ", WAREHOUSE_ID);
ELSE
 SET v_CON_=CONCAT(v_CON_," AND FAT_CODIGO_ARMAZEM IN (SELECT WAR_PERM_ARMAZEM_ID FROM GER_ARMAZEM_PERMISSOES
WHERE WAR_PERM_STATUS = 1 AND (WAR_PERM_PERFIL_ID =",PERFIL_ID," OR WAR_PERM_UTILIZADOR_ID = ",UTILIZADOR_ID,"))");
END IF;


SET v_CON_ = CONCAT(v_CON_," ORDER BY FAT_CODIGO_CLIENTE, FAT_DATA_EMISSAO, FAT_REFERENCIA, FAT_PARCELA");

SET @stmt_str = v_CON_;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;

END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for stp_COM_FATURA_CLIENTE_OBTERPORFILTRO
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_COM_FATURA_CLIENTE_OBTERPORFILTRO`;
delimiter ;;
CREATE PROCEDURE `stp_COM_FATURA_CLIENTE_OBTERPORFILTRO`(IN FILIAL INT, IN EMISSAO_INI DATE, IN EMISSAO_TERM DATE, IN VALIDADE_INI DATE, IN VALIDADE_TERM DATE, REFERENCIA VARCHAR(12), CLIENTE VARCHAR(120), DOCUMENTO INT, SITUACAO INT, in ENTIDADE INT, in DOCUMENT_STATUS int, IN PAYMENT_STATUS INT, IN SERIE INT, WAREHOUSE_ID INT, UTILIZADOR VARCHAR(20), PRODUCT_ID INT,  PRODUCT_NAME VARCHAR(120), CATEGORY_ID INT, DOCUMENT_TYPE VARCHAR(10),
IN FISCAL_YEAR INT)
BEGIN
	#Routine body goes here...
DECLARE v_CON_ LONGTEXT;
DECLARE PERFIL_ID INT DEFAULT 0;
DECLARE UTILIZADOR_ID INT DEFAULT 0;

SELECT UTI_CODIGO_PERFIL, UTI_CODIGO INTO PERFIL_ID, UTILIZADOR_ID FROM SIS_UTILIZADOR WHERE UTI_UTILIZADOR = UTILIZADOR;

UPDATE COM_FATURA_CLIENTE SET FAT_VALOR_SALDO = 0, FAT_PAID = 1, FAT_STATUS_PAGAMENTO = 3
WHERE FAT_VALOR_PAGO = (FAT_VALOR_TOTAL - FAT_VALOR_RETENCAO) AND FAT_VALOR_SALDO !=0;

UPDATE COM_FATURA_CLIENTE SET FAT_STATUS = 0
WHERE FAT_STATUS_DOCUMENTO = 7;

UPDATE COM_FATURA_CLIENTE SET FAT_STATUS_DOCUMENTO = 7 
WHERE FAT_STATUS = 0;

UPDATE COM_FATURA_CLIENTE SET FAT_STATUS = 1
WHERE FAT_STATUS_DOCUMENTO != 7;


SET v_CON_ = CONCAT("

SELECT DISTINCT
	C.FAT_CODIGO, 
	C.FAT_REFERENCIA, 
	C.FAT_DATA_EMISSAO, 
	DOC_DESCRICAO, 
	UPPER(C.FAT_NOME_CLIENTE) AS FAT_NOME_CLIENTE, 
	C.FAT_TOTAL_DESCONTOS, 
	C.FAT_TOTAL_IMPOSTOS, 
	(C.FAT_VALOR_TOTAL - IFNULL(C.FAT_CUSTOMER_CREDIT, 0)) AS FAT_VALOR_TOTAL,
  C.FAT_VALOR_PAGO,
  C.FAT_TROCO,
  C.FAT_DATA_VALIDADE,
  C.FAT_VALOR_SALDO,
  C.FAT_STATUS_DOCUMENTO,
  C.FAT_STATUS_PAGAMENTO,
  SER_DESCRICAO,
  C.FAT_VALOR_BRUTO,
  C.FAT_CREATED_BY,
  DOC_FORMATO,
  DOC_CODIGO,
  ENT_EMAIL,
  C.FAT_CAMBIO,
  C.FAT_CODIGO_CLIENTE,
  C.FAT_PARCELA,
	C.FAT_LOCAL_DESCARGA,
  C.FAT_MORADA_DESCARGA,
  C.FAT_DATA_ENTREGA,
  C.FAT_RECEPCAO,
  C.FAT_RESPONSAVEL_ENTREGA,
  O.FAT_REFERENCIA AS DOCUMENTO_ORIGINAL,
  C.FAT_STATUS,
  C.FAT_CREATED_DATE, 
  ENT_IDENTIFICACAO,
  C.FAT_NUMERACAO,
	C.FAT_VALOR_RETENCAO,
	IFNULL(C.FAT_CUSTOMER_CREDIT, 0) AS FAT_CUSTOMER_CREDIT, #Valor Rectificado por Nota de Crédito ou Recibo de Regularizacao de Cliente 
	SER_CODIGO
FROM 
COM_FATURA_CLIENTE C 
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = C.FAT_CODIGO_DOCUMENTO
INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = C.FAT_CODIGO_SERIE
LEFT JOIN GER_ENTIDADE ON ENT_CODIGO = C.FAT_CODIGO_CLIENTE
LEFT JOIN COM_FATURA_CLIENTE O ON O.FAT_CODIGO = C.FAT_NUMERO_DOC_ORIGEM
LEFT JOIN COM_FATURA_CLIENTE_ITEM ON FAT_ITEM_CODIGO_FATURA=C.FAT_CODIGO
LEFT JOIN GER_ARTIGO ON ART_CODIGO = FAT_ITEM_CODIGO_ARTIGO

WHERE C.FAT_CODIGO_FILIAL=",FILIAL, " AND C.FAT_REFERENCIA LIKE '%",REFERENCIA,"%' AND 
C.FAT_NOME_CLIENTE LIKE '%",CLIENTE,"%' ");

IF((EMISSAO_INI !='' AND EMISSAO_INI IS NOT NULL) AND (EMISSAO_TERM !='' AND EMISSAO_TERM IS NOT NULL)) THEN
  SET v_CON_ = CONCAT(v_CON_," AND C.FAT_DATA_EMISSAO BETWEEN '",EMISSAO_INI, "' AND '", EMISSAO_TERM, "'");
ELSE IF(EMISSAO_INI IS NOT NULL AND EMISSAO_TERM IS NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND C.FAT_DATA_EMISSAO >= '",EMISSAO_INI, "'");
ELSE IF(EMISSAO_INI IS NULL AND EMISSAO_TERM IS NOT NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND C.FAT_DATA_EMISSAO <= '", EMISSAO_TERM, "'");
END IF;
END IF;
END IF;

IF( VALIDADE_INI IS NOT NULL AND  VALIDADE_TERM IS NOT NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND C.FAT_DATA_VALIDADE BETWEEN '", VALIDADE_INI, "' AND '",  VALIDADE_TERM, "'");
ELSE IF( VALIDADE_INI IS NOT NULL AND  VALIDADE_TERM IS NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND C.FAT_DATA_VALIDADE >= '", VALIDADE_INI, "'");
ELSE IF( VALIDADE_INI IS NULL AND  VALIDADE_TERM IS NOT NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND C.FAT_DATA_VALIDADE <= '",  VALIDADE_TERM, "'");
END IF;
END IF;
END IF;
IF(DOCUMENTO > 0) THEN
SET v_CON_=CONCAT(v_CON_," AND C.FAT_CODIGO_DOCUMENTO = ", DOCUMENTO);
END IF;
/*
IF(SITUACAO > 0) THEN
 SET v_CON_=CONCAT(v_CON_," AND C.FAT_STATUS = 1 ");
END IF; 
*/
IF(DOCUMENT_STATUS > 0) THEN
 SET v_CON_=CONCAT(v_CON_," AND C.FAT_STATUS_DOCUMENTO = ", DOCUMENT_STATUS);
  IF(DOCUMENT_STATUS = 8) THEN
    SET v_CON_=CONCAT(v_CON_," AND C.FAT_NUMERACAO > 0 ");
  END IF;
END IF; 

IF(PAYMENT_STATUS > 0) THEN
 SET v_CON_=CONCAT(v_CON_," AND C.FAT_STATUS_PAGAMENTO = ", PAYMENT_STATUS);
END IF;
 
IF(ENTIDADE > 0) THEN
SET v_CON_=CONCAT(v_CON_," AND C.FAT_CODIGO_CLIENTE = ", ENTIDADE); 
END IF;


IF(SERIE > 0) THEN
	SET v_CON_=CONCAT(v_CON_," AND C.FAT_CODIGO_SERIE = ", SERIE);
ELSE IF(FISCAL_YEAR > 0) THEN 
  SET v_CON_=CONCAT(v_CON_," AND SER_ANO = ", FISCAL_YEAR);
END IF;
END IF;

IF(PRODUCT_ID > 0) THEN  
 SET v_CON_=CONCAT(v_CON_," AND FAT_ITEM_CODIGO_ARTIGO =", PRODUCT_ID);
END IF;

IF(CATEGORY_ID > 0) THEN  
 SET v_CON_=CONCAT(v_CON_," AND ART_CATEGORIA =", CATEGORY_ID);
END IF;


SET v_CON_ = CONCAT(v_CON_," AND (ART_DESIGNACAO LIKE '%",PRODUCT_NAME,"%' OR ART_REFERENCIA LIKE '%",PRODUCT_NAME,"%' OR ART_CODIGO_BARRAS LIKE '%",PRODUCT_NAME,"%')");



IF(WAREHOUSE_ID > 0) THEN
 SET v_CON_=CONCAT(v_CON_, " AND C.FAT_CODIGO_ARMAZEM = ", WAREHOUSE_ID);
ELSE
 SET v_CON_=CONCAT(v_CON_," AND C.FAT_CODIGO_ARMAZEM IN (SELECT WAR_PERM_ARMAZEM_ID FROM GER_ARMAZEM_PERMISSOES
WHERE WAR_PERM_STATUS = 1 AND (WAR_PERM_PERFIL_ID =",PERFIL_ID," OR WAR_PERM_UTILIZADOR_ID = ",UTILIZADOR_ID,"))");
END IF;

IF(DOCUMENT_TYPE IS NOT NULL AND DOCUMENT_TYPE!="" AND DOCUMENT_TYPE!="-1") THEN

 IF(DOCUMENT_TYPE="INVOICE_D") THEN
  SET v_CON_=CONCAT(v_CON_," AND (DOC_FORMATO ='INVOICE_D' OR DOC_FORMATO ='INVOICE_P' OR DOC_FORMATO='INVOICE_A')");
 ELSE 
  SET v_CON_=CONCAT(v_CON_," AND DOC_FORMATO ='", DOCUMENT_TYPE,"'");
 END IF;
/*
ELSE
	SET v_CON_=CONCAT(v_CON_," AND (DOC_FORMATO ='INVOICE' OR DOC_FORMATO = 'INVOICE_R' OR DOC_FORMATO ='INVOICE_D' OR DOC_FORMATO='INVOICE_A')");*/
END IF;
	SET v_CON_ = CONCAT(v_CON_," ORDER BY C.FAT_CODIGO, C.FAT_NUMERACAO, C.FAT_REFERENCIA, C.FAT_DATA_EMISSAO");

#SELECT v_CON_;
SET @stmt_str = v_CON_;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;

END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for stp_COM_FATURA_CLIENTE_OBTERPORPK
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_COM_FATURA_CLIENTE_OBTERPORPK`;
delimiter ;;
CREATE PROCEDURE `stp_COM_FATURA_CLIENTE_OBTERPORPK`(IN `CODIGO` INT)
BEGIN
	#Routine body goes here...
DECLARE REFERENCIA VARCHAR(20) DEFAULT "";
DECLARE ISDRAFT INT DEFAULT 0;

SELECT FAT_REFERENCIA, FAT_NUMERACAO INTO REFERENCIA, ISDRAFT FROM COM_FATURA_CLIENTE WHERE FAT_CODIGO = CODIGO;

IF(ISDRAFT IS NULL OR ISDRAFT <=0) THEN

SELECT FAT_CODIGO, FAT_CODIGO_FILIAL, FAT_CODIGO_ARMAZEM, FAT_CODIGO_DOCUMENTO, FAT_CODIGO_SERIE, FAT_CODIGO_CLIENTE, FAT_DATA_EMISSAO,
	FAT_DATA_VALIDADE, FAT_CODIGO_MOEDA, IFNULL(FAT_CAMBIO, 1) AS FAT_CAMBIO , FAT_CONDICAO_PAGAMENTO, FAT_CODIGO_EXPEDICAO, SUM(FAT_DESCONTO) AS FAT_DESCONTO,
	FAT_NUMERACAO, 
	FAT_REFERENCIA, FAT_STATUS_DOCUMENTO, SUM(FAT_VALOR_BRUTO) AS FAT_VALOR_BRUTO, SUM(FAT_TOTAL_DESCONTOS) AS FAT_TOTAL_DESCONTOS, 
	SUM(FAT_TOTAL_IMPOSTOS) AS FAT_TOTAL_IMPOSTOS, SUM(FAT_VALOR_TOTAL - IFNULL(FAT_CUSTOMER_CREDIT, 0)) AS FAT_VALOR_TOTAL, FAT_STATUS_PAGAMENTO, 
	FAT_OBSERVACOES, FAT_DOCUMENTO_ORIGEM, FAT_NUMERO_DOC_ORIGEM, SUM(FAT_VALOR_PAGO) AS FAT_VALOR_PAGO, SUM(FAT_VALOR_SALDO) AS FAT_VALOR_SALDO, 
	SUM(FAT_TROCO) AS FAT_TROCO, FAT_NOME_CLIENTE, SUM(FAT_DESCONTO_CLIENTE) AS FAT_DESCONTO_CLIENTE, 
	FAT_DATA_CARGA, FAT_MORADA_CARGA, FAT_LOCAL_CARGA, FAT_DATA_DESCARGA, FAT_MORADA_DESCARGA, FAT_LOCAL_DESCARGA, 
	FAT_NOTAS_INTERNAS, FAT_MOTIVO_ANULACAO, FAT_STATUS, DOC_DESCRICAO, FAT_STATUS_DOCUMENTO, FAT_PARCELA, FAT_NOTAS_COMERCIAIS,
	FAT_RESPONSAVEL_CARGA, FAT_RESPONSAVEL_ENTREGA, FAT_RECEPCAO, FAT_CONTACTO_DESTINO, FAT_CONTACTO_DESTINO_ALT, FAT_ENTITY_BILL_ID,
	FAT_MATRICULA_CARGA, FAT_BARCODE, FAT_CODIGO_VENDEDOR, V.ENT_NOME_COMPLETO AS VENDEDOR, UTI_NOME AS FAT_CREATED_BY, FAT_CREATED_DATE,
	C.ENT_IDENTIFICACAO, FAT_CUSTOMER_SENT, FAT_HASH, FAT_PRIOR_HASH, FAT_VALOR_RETENCAO, MOE_SIGLA, DOC_COMENTARIO_FIXO, FAT_TITLE, DOC_FORMATO, FAT_VALOR_TOTAL_UTENTE, FAT_VALOR_TOTAL_ENTIDADE, C.ENT_EMAIL, C.ENT_TELEFONE,
	FAT_FTM_NUMERO, FAT_FTM_REFERENCIA, FAT_FTM_DATA, FAT_MATRICULA, FAT_MARCA, FAT_MODELO, FAT_ANO, FAT_CONTRATO, FAT_SESNUMBER, FAT_LOOKUPFIELD7, FAT_LOOKUPFIELD8, FAT_LOOKUPFIELD9, FAT_LOOKUPFIELD10, FAT_CODIGO_FILIAL_FATURACAO,
IFNULL((SELECT FAT_VALOR_TOTAL FROM COM_FATURA_CLIENTE WHERE FAT_CODIGO = F.FAT_NUMERO_DOC_ORIGEM), 0) AS FAT_VALOR_DOCUMENTO_ORIGINAL 

	FROM COM_FATURA_CLIENTE F
	INNER JOIN GER_ENTIDADE C ON C.ENT_CODIGO = FAT_CODIGO_CLIENTE
	INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = FAT_CODIGO_DOCUMENTO
	LEFT JOIN SIS_UTILIZADOR ON UTI_UTILIZADOR = FAT_CREATED_BY
	LEFT JOIN GER_ENTIDADE V ON V.ENT_CODIGO = FAT_CODIGO_VENDEDOR
	LEFT JOIN FIN_MOEDA ON MOE_CODIGO = FAT_CODIGO_MOEDA
	WHERE FAT_REFERENCIA = REFERENCIA AND FAT_CODIGO = CODIGO;
ELSE
SELECT FAT_CODIGO, FAT_CODIGO_FILIAL, FAT_CODIGO_ARMAZEM, FAT_CODIGO_DOCUMENTO, FAT_CODIGO_SERIE, FAT_CODIGO_CLIENTE, FAT_DATA_EMISSAO,
FAT_DATA_VALIDADE, FAT_CODIGO_MOEDA, IFNULL(FAT_CAMBIO, 1) AS FAT_CAMBIO , FAT_CONDICAO_PAGAMENTO, FAT_CODIGO_EXPEDICAO, SUM(FAT_DESCONTO) AS FAT_DESCONTO,
FAT_NUMERACAO, 
FAT_REFERENCIA, FAT_STATUS_DOCUMENTO, SUM(FAT_VALOR_BRUTO) AS FAT_VALOR_BRUTO, SUM(FAT_TOTAL_DESCONTOS) AS FAT_TOTAL_DESCONTOS, 
SUM(FAT_TOTAL_IMPOSTOS) AS FAT_TOTAL_IMPOSTOS, SUM(FAT_VALOR_TOTAL) AS FAT_VALOR_TOTAL, FAT_STATUS_PAGAMENTO, 
FAT_OBSERVACOES, FAT_DOCUMENTO_ORIGEM, FAT_NUMERO_DOC_ORIGEM, SUM(FAT_VALOR_PAGO) AS FAT_VALOR_PAGO, SUM(FAT_VALOR_SALDO) AS FAT_VALOR_SALDO, 
SUM(FAT_TROCO) AS FAT_TROCO, FAT_NOME_CLIENTE, SUM(FAT_DESCONTO_CLIENTE) AS FAT_DESCONTO_CLIENTE, 
FAT_DATA_CARGA, FAT_MORADA_CARGA, FAT_LOCAL_CARGA, FAT_DATA_DESCARGA, FAT_MORADA_DESCARGA, FAT_LOCAL_DESCARGA, 
FAT_NOTAS_INTERNAS, FAT_MOTIVO_ANULACAO, FAT_STATUS, DOC_DESCRICAO, FAT_STATUS_DOCUMENTO, FAT_PARCELA, FAT_NOTAS_COMERCIAIS,
FAT_RESPONSAVEL_CARGA, FAT_RESPONSAVEL_ENTREGA, FAT_RECEPCAO, FAT_CONTACTO_DESTINO, FAT_CONTACTO_DESTINO_ALT, FAT_ENTITY_BILL_ID,
FAT_MATRICULA_CARGA, FAT_BARCODE, FAT_CODIGO_VENDEDOR, V.ENT_NOME_COMPLETO AS VENDEDOR, UTI_NOME AS FAT_CREATED_BY, FAT_CREATED_DATE,
C.ENT_IDENTIFICACAO, FAT_CUSTOMER_SENT, FAT_HASH, FAT_PRIOR_HASH, FAT_VALOR_RETENCAO, MOE_SIGLA, DOC_COMENTARIO_FIXO, FAT_TITLE, DOC_FORMATO, FAT_VALOR_TOTAL_UTENTE, FAT_VALOR_TOTAL_ENTIDADE, C.ENT_EMAIL, C.ENT_TELEFONE,
	FAT_FTM_NUMERO, FAT_FTM_REFERENCIA, FAT_FTM_DATA, FAT_MATRICULA, FAT_MARCA, FAT_MODELO, FAT_ANO, FAT_CONTRATO, FAT_SESNUMBER, FAT_LOOKUPFIELD7, FAT_LOOKUPFIELD8, FAT_LOOKUPFIELD9, FAT_LOOKUPFIELD10, FAT_CODIGO_FILIAL_FATURACAO,
IFNULL((SELECT FAT_VALOR_TOTAL FROM COM_FATURA_CLIENTE WHERE FAT_CODIGO = F.FAT_NUMERO_DOC_ORIGEM), 0) AS FAT_VALOR_DOCUMENTO_ORIGINAL   
FROM COM_FATURA_CLIENTE F
INNER JOIN GER_ENTIDADE C ON C.ENT_CODIGO = FAT_CODIGO_CLIENTE
