delimiter ;;
CREATE PROCEDURE `stp_COM_FATURA_CLIENTE_OBTERVENDASDIARIAS`(IN `FILIAL` int,IN `INICIO` date,IN `TERMINO` date, WAREHOUSE_ID INT, UTILIZADOR VARCHAR(20))
BEGIN
	#Routine body goes here...
DECLARE PERFIL_ID INT DEFAULT 0;
DECLARE UTILIZADOR_ID INT DEFAULT 0;

DECLARE v_CON_ LONGTEXT;
SELECT UTI_CODIGO_PERFIL, UTI_CODIGO INTO PERFIL_ID, UTILIZADOR_ID FROM SIS_UTILIZADOR WHERE UTI_UTILIZADOR = UTILIZADOR;


IF(INICIO IS NULL) THEN 
SELECT MIN(FAT_DATA_EMISSAO) INTO INICIO FROM COM_FATURA_CLIENTE; 
END IF;

IF(TERMINO IS NULL) THEN 
SELECT MAX(FAT_DATA_EMISSAO) INTO TERMINO FROM COM_FATURA_CLIENTE; 
END IF;
SET v_CON_ = CONCAT("
SELECT FAT_DATA_EMISSAO, SUM(FAT_VALOR_TOTAL), SUM(FAT_TOTAL_IMPOSTOS), SUM(FAT_VALOR_BRUTO), SUM(FAT_TOTAL_DESCONTOS),
(
SELECT IFNULL(SUM(
(CASE WHEN MOV_TRANSACAO='E' THEN
		  MOV_VALOR 
		  WHEN (MOV_TRANSACAO='S' AND MOV_VALOR > 0 -MOV_TRANSACAO) THEN (MOV_VALOR *(-1))
     ELSE 0 
    END)
),0) AS MOV_VALOR FROM FIN_MOVIMENTO_CAIXA  
WHERE MOV_DATA = FAT_DATA_EMISSAO AND MOV_CODIGO_FILIAL = ",FILIAL,"
) AS RECEBIDO, IFNULL(SUM(FAT_VALOR_RETENCAO),0) AS FAT_VALOR_RETENCAO 

FROM COM_FATURA_CLIENTE
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = FAT_CODIGO_DOCUMENTO
WHERE DOC_FORMATO IN ('INVOICE', 'INVOICE_R') AND FAT_STATUS=1 AND
FAT_STATUS_DOCUMENTO NOT IN(7, 1) AND FAT_CODIGO_FILIAL=", FILIAL, " AND FAT_DATA_EMISSAO BETWEEN '", INICIO, "' AND '", TERMINO, "'");

IF(WAREHOUSE_ID > 0) THEN
 SET v_CON_=CONCAT(v_CON_, " AND D.FAT_CODIGO_ARMAZEM = ", WAREHOUSE_ID);
ELSE
 SET v_CON_=CONCAT(v_CON_," AND FAT_CODIGO_ARMAZEM IN (SELECT WAR_PERM_ARMAZEM_ID FROM GER_ARMAZEM_PERMISSOES
WHERE WAR_PERM_STATUS = 1 AND (WAR_PERM_PERFIL_ID =",PERFIL_ID," OR WAR_PERM_UTILIZADOR_ID = ",UTILIZADOR_ID,"))");
END IF;
SET v_CON_=CONCAT(v_CON_, " GROUP BY FAT_DATA_EMISSAO ");

SET @stmt_str = v_CON_;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;

END
;;
delimiter ;

