-- Procedure structure for stp_COM_FATURA_CLIENTE_SETREFERENCE
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_COM_FATURA_CLIENTE_SETREFERENCE`;
delimiter ;;
CREATE PROCEDURE `stp_COM_FATURA_CLIENTE_SETREFERENCE`(IN `CODIGO` int, IN `NUMERO_DOC_ORIGEM` int, IN MOTIVO_ANULACAO VARCHAR(225), IN UTILIZADOR VARCHAR(20), IN VALOR_TOTAL DECIMAL(18,5))
BEGIN
	#Routine body goes here...
DECLARE DOCUMENT_SIMBOL VARCHAR(10) DEFAULT "";
DECLARE DOCUMENT_NUMBER INT DEFAULT 0;
DECLARE ANO_FATURACAO INT DEFAULT 0;
DECLARE ACTIVO INT DEFAULT 1;
DECLARE PREFIXO VARCHAR(12);
DECLARE DOC_TYPE VARCHAR(10) DEFAULT "";
DECLARE PARCELA_PRINCIPAL VARCHAR(20) DEFAULT ""; 
DECLARE PRIOR_HASH LONGTEXT DEFAULT ""; 
DECLARE DOC_FORMATE VARCHAR(50) DEFAULT "";
DECLARE ORIGINAL_DOC_VALUE DECIMAL(18,5) DEFAULT 0;
DECLARE MAX_DOC_NUMBER INT DEFAULT 0;
DECLARE STATUS_DOCUMENT INT DEFAULT 0;
DECLARE SERIE INT DEFAULT 0;
DECLARE NUMERACAO INT DEFAULT 0;
DECLARE REFERENCIA VARCHAR(120) DEFAULT NULL;
DECLARE DOCUMENTO INT DEFAULT 0;


SELECT FAT_STATUS_DOCUMENTO, FAT_CODIGO_SERIE, FAT_CODIGO_DOCUMENTO, FAT_NUMERACAO INTO STATUS_DOCUMENT, SERIE, DOCUMENTO,
NUMERACAO FROM COM_FATURA_CLIENTE WHERE FAT_CODIGO = CODIGO;

IF(STATUS_DOCUMENT = 8) THEN
     

     IF(NUMERACAO IS NULL OR NUMERACAO < 1) THEN
		 
			   SELECT FAT_REFERENCIA INTO REFERENCIA FROM COM_FATURA_CLIENTE WHERE FAT_CODIGO = CODIGO;
			   
				 SELECT DOC_SIGLA, DOC_FORMATO INTO DOCUMENT_SIMBOL, DOC_FORMATE FROM COM_DOCUMENTO WHERE DOC_CODIGO = DOCUMENTO;
			   
				 SELECT SER_NUMERACAO, SER_CODIGO INTO DOCUMENT_NUMBER, ANO_FATURACAO FROM COM_SERIE_DOCUMENTACAO 
				 WHERE SER_CODIGO = SERIE;
				 
				 SELECT IFNULL(MAX(FAT_NUMERACAO),0) INTO MAX_DOC_NUMBER FROM COM_FATURA_CLIENTE WHERE FAT_CODIGO_SERIE = SERIE
				 AND FAT_NUMERACAO > 0;
				 
				 IF(MAX_DOC_NUMBER!=DOCUMENT_NUMBER) THEN
				  SET DOCUMENT_NUMBER =MAX_DOC_NUMBER; 
				 END IF;
				 
         SET DOCUMENT_NUMBER = DOCUMENT_NUMBER+1;
        IF(DOCUMENT_NUMBER < 10) THEN 
          SET PREFIXO = CONCAT("00000",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 10 AND DOCUMENT_NUMBER <100) THEN 
          SET PREFIXO = CONCAT("0000",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 100 AND DOCUMENT_NUMBER <1000) THEN 
          SET PREFIXO = CONCAT("000",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 1000 AND DOCUMENT_NUMBER <10000) THEN 
          SET PREFIXO = CONCAT("00",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 10000 AND DOCUMENT_NUMBER <100000) THEN 
          SET PREFIXO = CONCAT("0",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 100000) THEN 
          SET PREFIXO = DOCUMENT_NUMBER; 
        END IF;
        END IF;
        END IF;
        END IF;
        END IF;
        END IF;
			 SET REFERENCIA = CONCAT(DOCUMENT_SIMBOL," ",ANO_FATURACAO,"/",PREFIXO);
			 UPDATE COM_FATURA_CLIENTE SET
			 FAT_NUMERACAO = DOCUMENT_NUMBER,
			 FAT_REFERENCIA = REFERENCIA,
			 FAT_STATUS_DOCUMENTO =8
			 WHERE FAT_CODIGO = CODIGO;

			 UPDATE COM_SERIE_DOCUMENTACAO SET
			 SER_NUMERACAO = DOCUMENT_NUMBER #SER_NUMERACAO + 1
			 WHERE SER_CODIGO = SERIE;
			 
			 
			 
     END IF;
		
		SELECT FAT_HASH INTO PRIOR_HASH FROM COM_FATURA_CLIENTE WHERE FAT_NUMERACAO =(DOCUMENT_NUMBER-1) 
		AND FAT_CODIGO_SERIE = SERIE;
		
		UPDATE COM_FATURA_CLIENTE SET
		FAT_PRIOR_HASH  = PRIOR_HASH
		WHERE FAT_CODIGO = CODIGO;
		
		IF(DOC_FORMATE ="INVOICE_D" OR  DOC_FORMATE ="INVOICE_P") THEN
		  SELECT FAT_VALOR_TOTAL INTO ORIGINAL_DOC_VALUE  FROM COM_FATURA_CLIENTE WHERE FAT_CODIGO = NUMERO_DOC_ORIGEM;
			IF(ORIGINAL_DOC_VALUE = VALOR_TOTAL) THEN
			     CALL stp_COM_FATURA_CLIENTE_EXCLUIR(NUMERO_DOC_ORIGEM, UTILIZADOR, MOTIVO_ANULACAO);
				   UPDATE COM_FATURA_CLIENTE SET 
					 FAT_RECTIFIED = 0,
					 FAT_STATUS = 0,
					 FAT_STATUS_DOCUMENTO = 7,
					 FAT_RECTIFIER_DOCUMENT_ID = CODIGO
					 WHERE FAT_CODIGO = NUMERO_DOC_ORIGEM; 
		  ELSE
			   UPDATE COM_FATURA_CLIENTE SET 
				 FAT_RECTIFIED = 1,
				 FAT_RECTIFIER_DOCUMENT_ID = CODIGO,
				 FAT_CUSTOMER_CREDIT = IFNULL(FAT_CUSTOMER_CREDIT, 0) + VALOR_TOTAL
				 WHERE FAT_CODIGO = NUMERO_DOC_ORIGEM;
		  END IF; 
		END IF;
		
IF(DOC_FORMATE="INVOICE") THEN
INSERT INTO FIN_CONTA_PAGAR_RECEBER(CONTA_FILIAL_ID,
								CONTA_ENTIDADE,
								CONTA_DOCUMENT_ID,
								CONTA_REFERENCIA,
								CONTA_EMISSAO,
								CONTA_VENCIMENTO,
								CONTA_PARCELA_ID,
								CONTA_STATUS,
								CONTA_TITULO,
								CONTA_DESCRICAO,
								CONTA_NATUREZA,
								CONTA_RUBRICA_ID,
								CONTA_VALOR,
								CONTA_PENDENTE,
								CONTA_CREATED_BY,
								CONTA_CREATED_DATE,
								CONTA_UPDATED_BY,
								CONTA_UPDATED_DATE,
								CONTA_MOEDA_ID,
								CONTA_CAMBIO,
								CONTA_VALOR_INCIDENCIA,
								CONTA_VALOR_DESCONTO,
								CONTA_IVA,
								CONTA_RETENCAO)
								SELECT FAT_CODIGO_FILIAL, FAT_CODIGO_CLIENTE, FAT_CODIGO_DOCUMENTO, FAT_REFERENCIA, FAT_DATA_EMISSAO, FAT_DATA_VALIDADE, 1, 1, CONCAT(DOC_DESCRICAO, " ", FAT_REFERENCIA), "", "R", NULL, FAT_VALOR_TOTAL - FAT_VALOR_RETENCAO, FAT_VALOR_TOTAL - FAT_VALOR_RETENCAO,FAT_CREATED_BY, FAT_CREATED_DATE, FAT_UPDATE_BY, FAT_UPDATED_DATE, FAT_CODIGO_MOEDA, FAT_CAMBIO, FAT_TOTAL_LIQUIDO, FAT_TOTAL_DESCONTOS, FAT_TOTAL_IMPOSTOS, FAT_VALOR_RETENCAO  FROM COM_FATURA_CLIENTE
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = FAT_CODIGO_DOCUMENTO
WHERE FAT_CODIGO = CODIGO;
END IF;



END IF;  

END
;;
delimiter ;

