-- Procedure structure for stp_COM_FATURA_FORNECEDOR_ADICIONAR
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_COM_FATURA_FORNECEDOR_ADICIONAR`;
delimiter ;;
CREATE PROCEDURE `stp_COM_FATURA_FORNECEDOR_ADICIONAR`(IN `CODIGO` int,IN `FILIAL` int,IN `ARMAZEM` int,IN `DOCUMENTO` int,IN `SERIE` int,IN `FORNECEDOR` int, IN `NOME_FORNECEDOR` VARCHAR(120), IN `EMISSAO` datetime,IN `VALIDADE` datetime,IN `MOEDA` int,IN `CAMBIO` decimal(18,2),IN `PRAZO` int,IN `EXPEDICAO` int,IN `DESCONTO` decimal(18,2),IN `STATUS_DOCUMENT` int,IN `NUMERACAO` int,IN `REFERENCIA` varchar(120), IN `VALOR_BRUTO` decimal(18,5),  IN `TOTAL_DESCONTOS` decimal(18,5), IN `TOTAL_LIQUIDO` decimal(18,2), IN `TOTAL_IMPOSTOS` decimal(18,5),IN `VALOR_TOTAL` decimal(18,5), IN `STATUS_PAGTO` int, in `OBSERVACOES` VARCHAR(225), DOCUMENTO_ORIGEM int, NUMERO_DOC_ORIGEM int, VALOR_PAGO  decimal (18,5), SALDO  decimal (18,5), TROCO decimal (18,5), UTILIZADOR VARCHAR(20), IN DOCUMENTO_ACTIVO INT,  DESCONTO_FORNECEDOR DECIMAL (18,2), DATA_CARGA DATE, ENDERECO_CARGA VARCHAR(120), LOCAL_CARGA VARCHAR(120), DATA_DESCARGA DATETIME, ENDERECO_DESCARGA VARCHAR(120), LOCAL_DESCARGA VARCHAR(120), NOTAS_INTERNAS VARCHAR(225), MOTIVO_ANULACAO VARCHAR(225),  IN PARCELA INT, VENDEDOR INT, COMISSAO DECIMAL(18,2), BARCODE VARCHAR(50), PREVISAO_ENTREGA DATETIME, DATA_LIQUIDACAO DATETIME, LOADER_MAN INT, RECEPTOR VARCHAR(120), DELIVERY_MAN VARCHAR(120),  RETENCAO DECIMAL(18,2), BILLING_ID INT, NOTAS_COMERCIAIS LONGTEXT , TEL_DESTINO_1 VARCHAR(20), TEL_DESTINO_2 VARCHAR(20), MATRICULA VARCHAR(20), REFERENCIA_FORNECEDOR VARCHAR(50), VALOR_FRETE DECIMAL(18,5), VALOR_SEGURO DECIMAL(18,5), IVA_IMPORTACAO DECIMAL(18,5), VALOR_ADUANEIRO DECIMAL(18,5), HONORARIO_DESPACHANTE DECIMAL(18,5), TRANSPORTACAO_LOCAL DECIMAL(18,5), OUTROS_ENCARGOS DECIMAL(18,5), OUT LSTID INT)
BEGIN
	
DECLARE DOCUMENT_SIMBOL VARCHAR(3) DEFAULT "";
DECLARE DOCUMENT_NUMBER INT DEFAULT 0;
DECLARE ANO_FATURACAO INT DEFAULT 0;
DECLARE ACTIVO INT DEFAULT 1;
DECLARE PREFIXO VARCHAR(12);
DECLARE DOC_TYPE VARCHAR(10) DEFAULT "";
DECLARE PARCELA_PRINCIPAL VARCHAR(20) DEFAULT "";
DECLARE TOTAL_ENCARGOS DECIMAL(18,5) DEFAULT 0; 

SET TOTAL_ENCARGOS = VALOR_FRETE + VALOR_SEGURO + IVA_IMPORTACAO + VALOR_ADUANEIRO + HONORARIO_DESPACHANTE + TRANSPORTACAO_LOCAL + OUTROS_ENCARGOS;

IF(VALOR_PAGO =0) THEN
 SET SALDO = VALOR_TOTAL;

END IF;

IF(VALOR_PAGO = VALOR_TOTAL) THEN
SET SALDO =0;
SET TROCO =0;
END IF;

IF(DOCUMENTO_ORIGEM = -1) THEN
SET DOCUMENTO_ORIGEM = NULL;
SET NUMERO_DOC_ORIGEM = NULL;
END IF;

IF(REFERENCIA IS NOT NULL AND REFERENCIA !="") THEN
  SELECT FAT_CODIGO INTO CODIGO FROM COM_FATURA_FORNECEDOR WHERE FAT_REFERENCIA = REFERENCIA AND FAT_PARCELA = PARCELA;
END IF;
  

IF(CODIGO <= 0) THEN

IF(REFERENCIA IS NULL OR REFERENCIA ="")THEN

SELECT SER_DESCRICAO INTO REFERENCIA FROM COM_SERIE_DOCUMENTACAO WHERE SER_CODIGO = SERIE;
