	FAT_STATUS_DOCUMENTO, FAT_VALOR_BRUTO, FAT_TOTAL_LIQUIDO , FAT_TOTAL_DESCONTOS , FAT_TOTAL_IMPOSTOS , FAT_VALOR_TOTAL, 
	FAT_STATUS_PAGAMENTO, FAT_OBSERVACOES, FAT_DOCUMENTO_ORIGEM, FAT_NUMERO_DOC_ORIGEM, FAT_VALOR_PAGO, FAT_VALOR_SALDO, FAT_TROCO, 
	FAT_CREATED_BY, FAT_CREATED_DATE, FAT_UPDATED_DATE, FAT_UPDATE_BY, FAT_STATUS, FAT_DESCONTO_FORNECEDOR, FAT_DATA_CARGA, FAT_MORADA_CARGA,
	FAT_LOCAL_CARGA, FAT_DATA_DESCARGA, FAT_MORADA_DESCARGA, FAT_LOCAL_DESCARGA, FAT_NOTAS_INTERNAS, FAT_MOTIVO_ANULACAO,FAT_PARCELA,
  FAT_CODIGO_COMPRADOR, FAT_BARCODE, FAT_DATA_ENTREGA, FAT_DATA_LIQUIDACAO, FAT_RESPONSAVEL_CARGA, FAT_RECEPCAO, FAT_RESPONSAVEL_ENTREGA, 
  FAT_VALOR_RETENCAO, FAT_VALOR_COMISSOES, FAT_ENTITY_BILL_ID,  FAT_NOTAS_COMERCIAIS, FAT_CONTACTO_DESTINO, FAT_CONTACTO_DESTINO_ALT,
  FAT_MATRICULA_CARGA, FAT_REFERENCIA_FORNECEDOR, FAT_VALOR_FRETE, FAT_VALOR_SEGURO, FAT_IVA_IMPORTACAO, FAT_VALOR_ADUANEIRA, FAT_VALOR_HONORARIO_DESPACHANTE, FAT_VALOR_TRANSPORTACAO_LOCAL, FAT_OUTROS_ENCARGOS,
FAT_TOTAL_ENCARGOS
) 
	VALUES
	(FILIAL, ARMAZEM, DOCUMENTO, SERIE, FORNECEDOR, NOME_FORNECEDOR, EMISSAO, VALIDADE, MOEDA, CAMBIO, PRAZO, EXPEDICAO, 
	DESCONTO, NUMERACAO, REFERENCIA, STATUS_DOCUMENT, VALOR_BRUTO, TOTAL_LIQUIDO, TOTAL_DESCONTOS, TOTAL_IMPOSTOS, VALOR_TOTAL,
	STATUS_PAGTO, OBSERVACOES, DOCUMENTO_ORIGEM, NUMERO_DOC_ORIGEM, VALOR_PAGO, SALDO, TROCO, UTILIZADOR, NOW(), NOW(), UTILIZADOR, 
	ACTIVO, DESCONTO_FORNECEDOR, DATA_CARGA, ENDERECO_CARGA, LOCAL_CARGA, DATA_DESCARGA, ENDERECO_DESCARGA, LOCAL_DESCARGA, 
  NOTAS_INTERNAS, MOTIVO_ANULACAO, PARCELA, VENDEDOR, BARCODE, PREVISAO_ENTREGA, DATA_LIQUIDACAO, LOADER_MAN, RECEPTOR, DELIVERY_MAN, RETENCAO,
COMISSAO, BILLING_ID, NOTAS_COMERCIAIS,TEL_DESTINO_1, TEL_DESTINO_2, MATRICULA, REFERENCIA_FORNECEDOR, VALOR_FRETE, VALOR_SEGURO, IVA_IMPORTACAO, VALOR_ADUANEIRO, HONORARIO_DESPACHANTE, TRANSPORTACAO_LOCAL, OUTROS_ENCARGOS, 
TOTAL_ENCARGOS);
	SELECT LAST_INSERT_ID() INTO CODIGO;
ELSE
UPDATE COM_FATURA_FORNECEDOR SET
FAT_CODIGO_FILIAL = FILIAL, 
FAT_CODIGO_ARMAZEM = ARMAZEM, 
FAT_CODIGO_DOCUMENTO = DOCUMENTO, 
