FAT_CODIGO_FORNECEDOR = FORNECEDOR, 
FAT_NOME_FORNECEDOR = NOME_FORNECEDOR,
FAT_DATA_VALIDADE = VALIDADE,
FAT_CODIGO_MOEDA = MOEDA, 
FAT_CAMBIO = CAMBIO, 
FAT_CONDICAO_PAGAMENTO = PRAZO, 
FAT_CODIGO_EXPEDICAO = EXPEDICAO, 
FAT_DESCONTO = DESCONTO,
FAT_STATUS_DOCUMENTO = STATUS_DOCUMENT, 
FAT_VALOR_BRUTO = VALOR_BRUTO,
FAT_TOTAL_LIQUIDO = TOTAL_LIQUIDO, 
FAT_TOTAL_DESCONTOS = TOTAL_DESCONTOS , 
FAT_TOTAL_IMPOSTOS = TOTAL_IMPOSTOS , 
FAT_VALOR_TOTAL = VALOR_TOTAL,
FAT_STATUS_PAGAMENTO = STATUS_PAGTO, 
