-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_COM_FATURA_FORNECEDOR_CANCELAR`;
delimiter ;;
CREATE PROCEDURE `stp_COM_FATURA_FORNECEDOR_CANCELAR`(IN `CODIGO` int)
BEGIN
	#Routine body goes here...
DECLARE NUMERACAO INT DEFAULT CODIGO;
DECLARE SERIE_ID INT DEFAULT 0;
DECLARE STOCK_MOV INT DEFAULT 0;
DECLARE QUANTIDADE DECIMAL(18,5) DEFAULT 0;
DECLARE PRODUCT_ID INT DEFAULT 0;
DECLARE MOVIMENTO INT DEFAULT 0;


WHILE(EXISTS(SELECT * FROM STOCK_MOVIMENTO_DIRECTOS WHERE MOV_DOCUMENT_ID = CODIGO AND MOV_DELETED!=1)) DO
  SELECT MOV_CODIGO INTO STOCK_MOV FROM STOCK_MOVIMENTO_DIRECTOS WHERE MOV_DOCUMENT_ID = CODIGO LIMIT 1;
  SELECT IFNULL(MOV_STK_ART_EXISTENCIA,0), MOV_STK_ART_CODIGO_ARTIGO INTO QUANTIDADE, PRODUCT_ID FROM STOCK_MOVIMENTOS_DIRECTOS_ARTIGOS WHERE MOV_STK_ART_CODIGO_MOVIMENTO = STOCK_MOV;
  DELETE FROM STOCK_MOVIMENTOS_DIRECTOS_ARTIGOS WHERE MOV_STK_ART_CODIGO_MOVIMENTO = STOCK_MOV;
  IF(QUANTIDADE IS NOT NULL AND QUANTIDADE !=0) THEN
       
      IF(QUANTIDADE < 0) THEN
       SET QUANTIDADE = QUANTIDADE * (-1);
      END IF;
      UPDATE COM_STOCK SET STOCK_QUANTIDADE = STOCK_QUANTIDADE + QUANTIDADE WHERE STOCK_CODIGO_ARTIGO = PRODUCT_ID;
  END IF;
END WHILE;
SELECT MOV_CODIGO_MOVIMENTO INTO MOVIMENTO FROM FIN_MOVIMENTO_COMPRA WHERE MOV_CODIGO_COMPRA = CODIGO LIMIT 1; 

DELETE FROM COM_FATURA_FORNECEDOR_ITEM WHERE FAT_ITEM_CODIGO_COMPRA = CODIGO;
DELETE FROM COM_POS_MOVIMENTO WHERE POS_MOV_CODIGO_MOVIMENTO = MOVIMENTO; 
DELETE FROM FIN_MOVIMENTO_COMPRA WHERE MOV_CODIGO_COMPRA = CODIGO;
DELETE FROM FIN_MOVIMENTO_CAIXA  WHERE MOV_CODIGO = MOVIMENTO;
DELETE FROM COM_FATURA_FORNECEDOR WHERE FAT_CODIGO = CODIGO;

SELECT FAT_CODIGO_SERIE INTO SERIE_ID FROM COM_FATURA_FORNECEDOR WHERE FAT_CODIGO = CODIGO;
UPDATE COM_SERIE_DOCUMENTACAO SET SER_NUMERACAO = NUMERACAO -1 WHERE SER_CODIGO = SERIE_ID;

END
;;
