-- Procedure structure for stp_COM_FATURA_FORNECEDOR_EXCLUIR
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_COM_FATURA_FORNECEDOR_EXCLUIR`;
delimiter ;;
CREATE PROCEDURE `stp_COM_FATURA_FORNECEDOR_EXCLUIR`(IN `CODIGO` INT, IN UTILIZADOR VARCHAR(20), IN MOTIVO VARCHAR(120))
BEGIN
	#Routine body goes here...
DECLARE MOVIMENTO INT DEFAULT 0;
DECLARE TOTAL_DOCS INT DEFAULT 0;
DECLARE RECIBO_ID INT DEFAULT 0;
DECLARE RECIBO_TYPE INT DEFAULT 0; 


 SELECT COUNT(REC_DOC_DOCUMENTO_ID) INTO TOTAL_DOCS FROM COM_RECIBO_CLIENTE_DOCUMENTOS WHERE REC_DOC_DOCUMENTO_ID = CODIGO;
IF(TOTAL_DOCS IS NULL OR TOTAL_DOCS <=0) THEN
	UPDATE  COM_FATURA_FORNECEDOR SET
	FAT_STATUS =0,
	FAT_STATUS_DOCUMENTO = 7,
	FAT_MOTIVO_ANULACAO = MOTIVO,
	FAT_UPDATE_BY = UTILIZADOR,
	FAT_UPDATED_DATE = NOW()
	WHERE FAT_CODIGO = CODIGO;

	SELECT COUNT(MOV_CODIGO_FATURA) INTO TOTAL_DOCS FROM FIN_MOVIMENTO_COMPRA WHERE MOV_CODIGO_FATURA = CODIGO AND MOV_FAT_STATUS !=0; 
	WHILE(TOTAL_DOCS > 0) DO
	 SELECT MOV_CODIGO_MOVIMENTO INTO MOVIMENTO FROM FIN_MOVIMENTO_COMPRA WHERE MOV_CODIGO_FATURA = CODIGO LIMIT 1; 
	 UPDATE  COM_POS_MOVIMENTO SET POS_MOV_STATUS = 0 WHERE POS_MOV_CODIGO_MOVIMENTO = MOVIMENTO;
	 UPDATE FIN_MOVIMENTO_CAIXA SET MOV_STATUS =0 WHERE MOV_CODIGO = MOVIMENTO;
   UPDATE FIN_MOVIMENTO_COMPRA SET MOV_FAT_STATUS =0 WHERE MOV_CODIGO_FATURA = CODIGO; 
	 SELECT COUNT(MOV_CODIGO_FATURA) INTO TOTAL_DOCS FROM FIN_MOVIMENTO_COMPRA WHERE MOV_CODIGO_FATURA = CODIGO AND MOV_FAT_STATUS !=0;
	END WHILE; 

		UPDATE STOCK_MOVIMENTO_DIRECTOS SET
		MOV_DELETED = 1,
		MOV_DELETED_BY = UTILIZADOR,
		MOV_DELETED_DATE = NOW()
		WHERE MOV_DOCUMENT_ID = CODIGO; 

		UPDATE STOCK_MOVIMENTOS_DIRECTOS_ARTIGOS AS TD, STOCK_MOVIMENTO_DIRECTOS AS TM  SET
		TD.MOV_STK_STATUS =0,
		TD.MOV_STK_ART_UPDATED_BY = UTILIZADOR,
		TD.MOV_STK_ART_UPDATED_DATE = NOW()
		WHERE TD.MOV_STK_ART_CODIGO_MOVIMENTO = TM.MOV_CODIGO AND TM.MOV_DOCUMENT_ID = CODIGO; 
    
		UPDATE COM_FATURA_FORNECEDOR_ITEM SET
		FAT_ITEM_STATUS = 0 
		WHERE FAT_ITEM_CODIGO_FATURA = CODIGO;
END IF;

END
;;
