CREATE PROCEDURE `stp_COM_FATURA_FORNECEDOR_ITEM_ADICIONAR`(IN `ARTIGO` int,IN `COMPRA` int,IN `PRECO` decimal(18,5), IN `QUANTIDADE` decimal(18,5), IN `DESCONTO` decimal(18,5), IN DESC_COMPRA decimal(18,5), IN `IMPOSTOS` decimal(18, 5), VALOR_DESC decimal(18,5), VALOR_IMP decimal(18,5),  IN `TOTAL` decimal(18,5), IN COMENTARIOS VARCHAR(255), ORDEM INT,  ITEM_STATUS INT, COMPOSICAO_ID INT, LOTE_ID INT, DIMENSAO_ID INT, QTD_ENCOMENDADA DECIMAL(18,2), QTD_RESERVADA DECIMAL(18,2), RETENCAO DECIMAL(18,2), ORIGEM_ID INT, ORIGEM_LINE INT, ENVIO_ID INT, ENVIO_LINE INT, DATA_ENTREGA DATETIME, DESC_FIN DECIMAL(18,2), VALOR_DESC_FIN DECIMAL(18,2), DESCONTO_NUMERARIO DECIMAL(18,2), SERIALNUMBER_ID INT, WAREHOUSE_ID INT, IN PRECO_CUSTO DECIMAL(18,5), VALOR_FRETE DECIMAL(18,5), VALOR_SEGURO DECIMAL(18,5), IVA_IMPORTACAO DECIMAL(18,5), VALOR_ADUANEIRO DECIMAL(18,5), HONORARIO_DESPACHANTE DECIMAL(18,5), TRANSPORTACAO_LOCAL DECIMAL(18,5), OUTROS_ENCARGOS DECIMAL(18,5))
BEGIN
	#Routine body goes here...
DECLARE CODIGO INT DEFAULT 0;
DECLARE DOC_TYPE VARCHAR(10);
DECLARE DOC_ID INT DEFAULT 0;
DECLARE PRODUCT_MARGEM DECIMAL(18,2) DEFAULT 0;
DECLARE TOTAL_ENCARGOS DECIMAL(18,5) DEFAULT 0; 

SET TOTAL_ENCARGOS = VALOR_FRETE + VALOR_SEGURO + IVA_IMPORTACAO + VALOR_ADUANEIRO + HONORARIO_DESPACHANTE + TRANSPORTACAO_LOCAL + OUTROS_ENCARGOS;

SELECT FAT_ITEM_CODIGO INTO CODIGO FROM COM_FATURA_FORNECEDOR_ITEM WHERE 
FAT_ITEM_CODIGO_ARTIGO = ARTIGO AND FAT_ITEM_CODIGO_COMPRA = COMPRA;
IF(CODIGO IS NULL OR CODIGO =0) THEN
INSERT INTO COM_FATURA_FORNECEDOR_ITEM (
FAT_ITEM_CODIGO_ARTIGO, 
FAT_ITEM_CODIGO_COMPRA, 
FAT_ITEM_PRECO, 
FAT_ITEM_QUANTIDADE,
FAT_ITEM_DESCONTO_LINHA, 
FAT_ITEM_DESCONTO_COMPRA, 
FAT_ITEM_IMPOSTO, 
FAT_ITEM_VALOR_DESCONTOS, 
FAT_ITEM_VALOR_IMPOSTOS, 
FAT_ITEM_TOTAL,
FAT_ITEM_COMENTARIOS, 
FAT_ORDEM, 
FAT_ITEM_STATUS,
FAT_ITEM_COMPOSICAO,
FAT_ITEM_LOTE_ID,
FAT_ITEM_DIMENSAO_ID,
FAT_ITEM_ENCOMENDADA,
FAT_ITEM_RESERVADA,
FAT_RETENCAO,
FAT_ITEM_DOC_ORIGEM_ID,
FAT_ITEM_DOC_ORIGEM_LINHA,
FAT_ITEM_ENVIO_DOC_ID,
FAT_ITEM_ENVIO_DOC_LINHA,
FAT_ITEM_DATA_ENTREGA,
FAT_ITEM_DESCONTO_FINANCEIRO,
FAT_ITEM_VALOR_DESCONTO_FINANCEIRO,
FAT_ITEM_DESCONTO_NUMERARIO_LINHA,
FAT_ITEM_SERIALNUMBER_ID,
FAT_ITEM_WAREHOUSE_ID,
FAT_ITEM_PRECO_CUSTO,
FAT_ITEM_VALOR_FRETE,
FAT_ITEM_VALOR_SEGURO,
FAT_ITEM_VALOR_IVA_IMPORTACAO,
FAT_ITEM_VALOR_ADUANEIRO,
FAT_ITEM_VALOR_HONORARIO_DESPACHANTE,
FAT_ITEM_VALOR_TRANSPORTACAO_LOCAL,
FAT_ITEM_VALOR_OUTROS_ENCARGOS,FAT_ITEM_VALOR_TOTAL
)
VALUES(ARTIGO, COMPRA, PRECO, QUANTIDADE, DESCONTO, DESC_COMPRA, IMPOSTOS, VALOR_DESC, VALOR_IMP, TOTAL, COMENTARIOS, ORDEM, ITEM_STATUS, COMPOSICAO_ID, LOTE_ID,
DIMENSAO_ID, QTD_ENCOMENDADA, QTD_RESERVADA, RETENCAO, ORIGEM_ID, ORIGEM_LINE, ENVIO_ID, ENVIO_LINE, DATA_ENTREGA, DESC_FIN, VALOR_DESC_FIN, DESCONTO_NUMERARIO, SERIALNUMBER_ID, WAREHOUSE_ID, PRECO_CUSTO, VALOR_FRETE, VALOR_SEGURO, IVA_IMPORTACAO, VALOR_ADUANEIRO, HONORARIO_DESPACHANTE, TRANSPORTACAO_LOCAL, OUTROS_ENCARGOS, 
TOTAL_ENCARGOS);

SELECT FAT_CODIGO_DOCUMENTO INTO DOC_ID FROM COM_FATURA_FORNECEDOR WHERE FAT_CODIGO = COMPRA;

SELECT DOC_FORMATO INTO DOC_TYPE FROM COM_DOCUMENTO WHERE DOC_CODIGO = DOC_ID;

IF(DOC_TYPE ="SUPPLIER_V" || DOC_TYPE="SUPPLIER_I") THEN
   
 SELECT ART_PRECO_CUSTO, ART_MARGEM_LUCRO INTO PRECO, PRODUCT_MARGEM FROM GER_ARTIGO WHERE ART_CODIGO = ARTIGO;

IF(PRECO_CUSTO != PRECO) THEN

		IF(PRODUCT_MARGEM > 0) THEN
			UPDATE GER_ARTIGO SET 
			ART_PRECO_CUSTO = PRECO_CUSTO, # UPDATE PRODUCT NEW/LAST COST PRICE
			ART_PRECO_VENDA = PRECO_CUSTO + (PRECO_CUSTO * (PRODUCT_MARGEM/100)) # UPDATE PRODUCT SALES PRICE
			WHERE ART_CODIGO = ARTIGO; 
		ELSE
			UPDATE GER_ARTIGO SET 
			ART_PRECO_CUSTO = PRECO_CUSTO 
			WHERE ART_CODIGO = ARTIGO; 
		END IF;

END IF;



END IF;


ELSE
UPDATE COM_FATURA_FORNECEDOR_ITEM SET
FAT_ITEM_PRECO = PRECO, 
FAT_ITEM_QUANTIDADE = FAT_ITEM_QUANTIDADE + QUANTIDADE,
FAT_ITEM_DESCONTO_LINHA = DESCONTO, 
FAT_ITEM_DESCONTO_COMPRA = DESC_COMPRA, 
