delimiter ;;
CREATE PROCEDURE `stp_COM_FATURA_FORNECEDOR_ITEM_OBTERPORFILTRO`(IN `COMPRA` int, INICIO DATE, FINAL DATE)
BEGIN
	#Routine body goes here...
IF(COMPRA > 0) THEN 

	SELECT I.*, ART_DESIGNACAO, ART_REFERENCIA, ART_CODIGO_BARRAS, UNI_SIGLA, ART_MOVIMENTA_STOCK, FAT_CODIGO_ARMAZEM,
	FAT_NOME_FORNECEDOR, FAT_RECEPCAO, FAT_LOCAL_DESCARGA, DOC_DESCRICAO, FAT_REFERENCIA, FAT_DATA_EMISSAO, FAT_DATA_DESCARGA 
  AS FAT_DATA_ENTREGA, DOC_FORMATO, (FAT_ITEM_VALOR_FRETE + FAT_ITEM_VALOR_SEGURO + FAT_ITEM_VALOR_IVA_IMPORTACAO +
FAT_ITEM_VALOR_ADUANEIRO + FAT_ITEM_VALOR_HONORARIO_DESPACHANTE + FAT_ITEM_VALOR_TRANSPORTACAO_LOCAL + FAT_ITEM_VALOR_OUTROS_ENCARGOS) AS FAT_ITEM_TOTAL_ENCARGOS
	FROM COM_FATURA_FORNECEDOR_ITEM I
	INNER JOIN GER_ARTIGO ON ART_CODIGO = FAT_ITEM_CODIGO_ARTIGO
	INNER JOIN GER_UNIDADE_MEDICAO ON UNI_CODIGO = ART_UNIDADE_VENDA
	INNER JOIN COM_FATURA_FORNECEDOR ON FAT_CODIGO = I.FAT_ITEM_CODIGO_COMPRA
	INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = FAT_CODIGO_DOCUMENTO
	WHERE FAT_ITEM_CODIGO_COMPRA = COMPRA ORDER BY FAT_ORDEM;

ELSE

	SELECT I.*, ART_DESIGNACAO, ART_REFERENCIA, ART_CODIGO_BARRAS, UNI_SIGLA, ART_MOVIMENTA_STOCK, FAT_CODIGO_ARMAZEM,
	FAT_NOME_FORNECEDOR, FAT_RECEPCAO, FAT_LOCAL_DESCARGA, DOC_DESCRICAO, FAT_REFERENCIA, FAT_DATA_EMISSAO, FAT_DATA_DESCARGA 
  AS FAT_DATA_ENTREGA, DOC_FORMATO, (FAT_ITEM_VALOR_FRETE + FAT_ITEM_VALOR_SEGURO + FAT_ITEM_VALOR_IVA_IMPORTACAO +
FAT_ITEM_VALOR_ADUANEIRO + FAT_ITEM_VALOR_HONORARIO_DESPACHANTE + FAT_ITEM_VALOR_TRANSPORTACAO_LOCAL + FAT_ITEM_VALOR_OUTROS_ENCARGOS) AS FAT_ITEM_TOTAL_ENCARGOS 
	FROM COM_FATURA_FORNECEDOR_ITEM I
	INNER JOIN GER_ARTIGO ON ART_CODIGO = FAT_ITEM_CODIGO_ARTIGO
	INNER JOIN GER_UNIDADE_MEDICAO ON UNI_CODIGO = ART_UNIDADE_VENDA
	INNER JOIN COM_FATURA_FORNECEDOR ON FAT_CODIGO = I.FAT_ITEM_CODIGO_COMPRA
	INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = FAT_CODIGO_DOCUMENTO
	WHERE FAT_DATA_ENTREGA BETWEEN INICIO AND FINAL ORDER BY FAT_ORDEM; 
END IF;
END
;;
