DROP PROCEDURE IF EXISTS `stp_COM_FATURA_FORNECEDOR_OBTERPORFILTRO`;
delimiter ;;
CREATE PROCEDURE `stp_COM_FATURA_FORNECEDOR_OBTERPORFILTRO`(IN FILIAL INT, IN EMISSAO_INI DATE, IN EMISSAO_TERM DATE, IN VALIDADE_INI DATE, IN VALIDADE_TERM DATE, REFERENCIA VARCHAR(12), FORNECEDOR VARCHAR(120), DOCUMENTO INT, SITUACAO INT, in ENTIDADE INT, in DOCUMENT_STATUS int, IN PAYMENT_STATUS INT, IN SERIE INT, WAREHOUSE_ID INT, UTILIZADOR VARCHAR(20), PRODUCT_ID INT, PRODUCT_NAME VARCHAR(120), CATEGORY_ID INT, DOCUMENT_TYPE VARCHAR(50), REFERENCIA_FORNECEDOR VARCHAR(50), ANO INT)
